#####	itempages_localized	#####

INSERT IGNORE INTO `itempages_localized` (`entry`) SELECT `entry` FROM `itempages`;
UPDATE `itempages_localized` SET `language_code`='deDE' WHERE `language_code`='';

SET NAMES 'utf8';

UPDATE `itempages_localized` SET `text`='Mein lieber Morgan,\n\ndie Geschäfte hier in Goldhain gehen gut - so gut, dass ich bislang noch nicht einmal dazugekommen bin, dir eine Lieferung zu schicken! \n\nDie Person, die dir diese Notiz übergibt, hat gleichzeitig auch den Auftrag von mir, dir ein großes Paket Wachskerzen zu übergeben - du weißt schon, diese Dinger, wie sie Kobolde gern auf dem Kopf tragen. \n\nBitte zeig dich dankbar und entlohn die Person angemessen.' WHERE (`entry`='15');
UPDATE `itempages_localized` SET `text`='An den ehrenwerten Direktor Crillian\n\nIch schreibe Euch, meinem ehemaligen Meister, um Euch wissen zu lassen, wie es Eurem früheren Lehrling ergangen ist. Ich habe Euren Rat befolgt und Wissen und Weisheit durch Reisen außerhalb unserer geliebten Heimat Sturmwind vergrößert. Meine Reise hat mich an viele Orte geführt, doch ich beschloss, meine Zelte hier in der schönen Stadt Mondbruch aufzuschlagen. Die umliegenden Felder von Westfall sehen so kurz vor der Erntezeit wunderschön aus.' WHERE (`entry`='16');
UPDATE `itempages_localized` SET `text`='Schon wenige Tage nach meinem Besuch avancierte ich zum Lehrer der Kinder von den umliegenden Gehöften. Es lief sogar so gut, dass der Bürgermeister mir die Leitung einer Schule übertragen hat, und nun ist der Bau einer ganz neuen Schule im Gange! Vom Silberwald nach Sturmwind und nun Mondbruch - wer hätte geglaubt, dass ich je so viel von Azeroth zu sehen bekommen würde!\n\nBeste Grüße\n\nStalvan Dunstmantel' WHERE (`entry`='17');
UPDATE `itempages_localized` SET `text`='Edler Sire,\r\n\r\nmir ist zu Ohren gekommen, dass Ihr einen Hauslehrer für Eure Kinder sucht. Ich habe mich vorübergehend in Goldhain im Gasthaus Zur Höhle des Löwen einquartiert. Ob der misslichen Lage in dem Gebiet war ich gezwungen, meine Stellung als Direktor der Schule in Mondbruch aufzugeben. Daher möchte ich mich bei Euch als Lehrer für Eure Sprösslinge bewerben. Ihr könnt Euch, falls nötig, bei Direktor Crillian von der Akademie über meine Fähigkeiten erkundigen.' WHERE (`entry`='18');
UPDATE `itempages_localized` SET `text`='Ich werde mich bemühen, Euch persönlich zu treffen, sobald die Winterregenfälle aufgehört haben und die Straßen wieder passierbar sind.\r\n\r\nBis dahin verbleibe ich\r\n\r\nEuer Stalvan Dunstmantel aus dem Silberwald.' WHERE (`entry`='19');
UPDATE `itempages_localized` SET `text`='...Giles, der Junge, scheint ziemlich wild zu sein und stellt eine echte Herausforderung für einen Lehrer dar. Tilloa hingegen, die ältere Tochter, scheint mir außerordentlich klug. Auch ihre atemberaubende Schönheit ist mir indes nicht verborgen geblieben. Sie steht nun an der Schwelle zur Weiblichkeit. Es steht zu vermuten, dass der Lord für das kommende Jahr ihre Heirat arrangiert hat. Aber ich schweife ab. Diese Woche werde ich die Familie zu ihrem Sommersitz in der Nähe des Holzfällerlagers des Osttals in Elwynn, nahe des Rotkammgebirges, begleiten. Dort kann ich hoffentlich mehr schreiben.' WHERE (`entry`='20');
UPDATE `itempages_localized` SET `text`='...merkwürdiges und überwältigendes Gefühl. Niemals zuvor habe ich so gefühlt wie heute. Ich half Giles bei seiner Geschichtsstunde, während sich Tilloa im Garten um die Blumen kümmerte. Nach einigen Minuten kam sie herein und drückte mir sanft eine scharlachrote Begonie in die Hand. Das Lächeln, das dabei um ihre Lippen spielte, ließ mein Herz in meiner Brust erbeben...' WHERE (`entry`='21');
UPDATE `itempages_localized` SET `text`='...habe keine Zweifel mehr, dass sie dasselbe auch für mich empfindet. Heute Morgen legte sie sogar kurz ihre Hand auf die meine. Wenn sie lächelt, leuchten ihre Augen wie glitzernde Diamanten. Ein Austausch ohne Worte - auch wenn wir schweigen, schlagen unsere Herzen im Gleichklang und halten unsere Sinne Zwiesprache.' WHERE (`entry`='22');
UPDATE `itempages_localized` SET `text`='...Zorn und Wut wie ich sie nie zuvor kannte! Wie kann sie es wagen. Ich unterrichtete Giles gerade in Zahlenkunde, als Tilloa nicht nur mit einem Verehrer auftauchte, sondern auch noch in aller Offenheit Händchen mit ihm hielt! Was für ein ungehobelter junger Mann. Und anstatt mich richtig vorzustellen, sagte Tilloa schlicht: "Oh, das ist nur mein Lehrer, Onkel Stalvan. Er ist ein netter alter Mann." Alt! Bei diesen Worten stieg mir die Zornesröte ins Gesicht. Ich bin nur ein paar Jahre älter und trotzdem betrügt sie...' WHERE (`entry`='23');
UPDATE `itempages_localized` SET `text`='...im Sog der Verzweiflung. Erst verhöhnt sie mich und nun ist sie verlobt. Das undankbare Gör hat mir Liebe vorgegaukelt, wo es mich doch nur verletzen wollte. Eine schwarze Leere hat mich erfasst und sie wird mit jedem Augenblick größer. Ihr Blut soll fließen, wie meine Tränen geflossen sind...' WHERE (`entry`='24');
UPDATE `itempages_localized` SET `text`='Tommy Joe -\nin jeder Sekunde, die ohne dich verstreicht, brennt mein Herz im Fegefeuer der Trennung von dir. Wenn unsere Familien doch weiter blicken könnten als nur bis zu jenen törichten Verbrechen, die sie sich angetan haben, wenn sie doch nur wüssten, dass das einzige wirkliche Verbrechen der Hass ist. Hass verzehrt, und ich fürchte, dass der Hass in jenen finsteren Zeiten um sich greifen und mehr erfassen wird als nur unsere Familien.\nWürden sie dies erkennen, könnten wir endlich zusammen sein. Auf diesen Tag hoffe ich und dafür lebe ich.\nIn Liebe\nMaybell' WHERE (`entry`='25');
UPDATE `itempages_localized` SET `text`='Mein lieber William,\n\nJahre ist es her, seit wir uns das letzte Mal gesprochen haben. Ich hoffe inständig, Euch und Eurem Bruder geht es gut und Eure Apothekerdienste sind gefragt.\n\nNun muss ich Euch um einen Gefallen bitten, William. Ich will keine langen Worte machen. Mein Enkel Tommy Joe hat sein Herz an die junge Maybell Maclure verloren. Und sie könnten sich innig lieben, wenn unsere Familien... tja, unsere Familien leben seit Jahren in Fehde.' WHERE (`entry`='26');
UPDATE `itempages_localized` SET `text`='Mein lieber William, Jahre ist es her, seit wir uns das letzte Mal gesprochen haben. Ich hoffe inständig, Euch und Eurem Bruder geht es gut und Eure Apothekerdienste sind gefragt. Nun muss ich Euch um einen Gefallen bitten, William. Ich will keine langen Worte machen. Mein Enkel Tommy Joe hat sein Herz an die junge Maybell Maclure verloren. Und sie könnten sich innig lieben, wenn unsere Familien... tja, unsere Familien leben seit Jahren in Fehde.' WHERE (`entry`='27');
UPDATE `itempages_localized` SET `text`='Argus, \n\nwie du weißt, stecke ich bis zum Hals in Reparaturaufträgen von der Armee. Ich will mich nicht über die viele Arbeit beklagen, nur geht mir allmählich das Eisen aus.\n\nIch habe nicht mal mehr genug, um Hufeisen zu schmieden. Und ich weiß, dass du immer viele Hufeisen auf Lager hast. Ich würde mir daher gern 50 Paar Hufeisen ausleihen, bis wieder eine Eisenlieferung eintrifft.\n\nVielen Dank im Voraus\nVerner' WHERE (`entry`='28');
UPDATE `itempages_localized` SET `text`='An der Kiste hängt eine Notiz. Darauf steht:\n\n"Verner - tut mir Leid zu hören, dass das Rotkammgebirge solchen Ärger hat. Hier sind die angeforderten Hufeisen. Ich möchte dafür umgehend 100 Silbertaler."\n\n"Du kannst mich aber auch in Unterbauchschuppen von schwarzen Großdrachenwelpen bezahlen (Ich habe gehört, Großdrachenwelpen seien in den Rotkammbergen keine Seltenheit.). Und weil wir Freunde sind … bin ich mit 4 Schuppen einverstanden. Danke - Argus"' WHERE (`entry`='29');
UPDATE `itempages_localized` SET `text`='Mein Lord,\n\nes herrscht Krieg. Uns droht die Verdammnis.\n\nAls Magistrat der Stadt Seenhain ist es meine Pflicht, Eurer Majestät von den jüngsten militärischen Aktivitäten in Eurer östlichsten Kolonie zu berichten.\n\nAuf den folgenden Seiten wird Euch Bericht erstattet, wie die Situation von Seenhain, hoch oben im Rotkammgebirge, ist.' WHERE (`entry`='30');
UPDATE `itempages_localized` SET `text`='Der Verlust von Burg Steinwacht wiegt schwer. Von der Allianz zwischen den Orcs des Blackrockklans und dem Hexenmeister namens Morganth habe ich Euch ja bereits berichtet. Die Blackrock haben fürchterlich gewütet. Sie standen zweifellos unter dem Einfluss der bösen Magie von Morganth. Viele der besten Soldaten Seiner Majestät haben bei der Verteidigung der Burg ihr Leben gelassen. Aber letztlich war alles umsonst.' WHERE (`entry`='31');
UPDATE `itempages_localized` SET `text`='Merkwürdigerweise ließ Morganth seine Orc-Truppen jedoch kurz nach dem brutalen Sieg umkehren. Der Hexenmeister ließ im Nordosten einen arkanen Turm errichten. Mit Hilfe seiner Macht schuf er sich eine kleine Armee aus Schattenfellgnollen, die jeden seiner bösartigen Befehle ohne Zögern ausführen. Sowohl Orcs als auch Männer sind der teuflischen Brut des Hexenmeisters schon zum Opfer gefallen. Was den Verrat zwischen Morganth und Gath''Ilzogg, dem Blackrockkriegsherrn angeht, so können wir zurzeit nur Vermutungen anstellen.' WHERE (`entry`='32');
UPDATE `itempages_localized` SET `text`='Zu allem Übel scheinen die Orcs alles daran zu setzen, die guten Untertanen Seiner Majestät abzuschlachten. In den vergangenen zwei Wochen hat der Blackrockklan eine ruchlose Offensive gegen Seenhain geführt. Viele tapfere Männer haben ihr Leben gelassen. Die Orcs rückten mit solcher Geschwindigkeit und Gewalt vor, dass die Immerruhbrücke einstürzte. Unsere Kriegsbestände gehen bedrohlich zu Neige. Es fehlt uns an Verbänden für die Verwundeten.' WHERE (`entry`='33');
UPDATE `itempages_localized` SET `text`='Ich bitte Ihre Majestät hiermit um Unterstützung der guten Menschen von Seenhain in dieser Zeit der Not. Wir brauchen nicht nur Soldaten, sondern auch neues Kriegsgerät als Ersatz für das, was wir in der Schlacht verloren haben. Auch Essen und Baumaterial werden knapp. Bitte, mein Lord, helft uns, die letzte Bastion zwischen Mensch und Orc zu halten. Der Feind darf nicht im Königreich Stormwind einfallen.' WHERE (`entry`='34');
UPDATE `itempages_localized` SET `text`='Doch ich befürchte, dass im Kessel der Verderbnis, den die Nordlande inzwischen darstellen, noch viel größeres und dunkleres Übel gärt. Es ist daher unbedingt notwendig, dass das Königreich Stormwind Maßnahmen ergreift, wollen wir nicht bald schon Relikte der Vergangenheit oder Sklaven des Feindes sein.\n\nGezeichnet\n\nMagistrat Solomon\nStadt Seenhain\nKönigreich Stormwind' WHERE (`entry`='35');
UPDATE `itempages_localized` SET `text`='Ehrenwerter Magistrat Solomon!\n\nIch trauere mit Euch um die Toten. Ich werde mich persönlich darum kümmern, dass der König von der Situation erfährt, und hoffe inständig, Verstärkung nach Seenhain schicken zu können. Doch zeigte sich seine Majestät in letzter Zeit zerstreut und benahm sich etwas merkwürdig. Ich sage Euch dies ganz im Vertrauen.\n\nAuf bald\n\nGeneral Marcus Jonathan\nKönigreich Sturmwind' WHERE (`entry`='36');
UPDATE `itempages_localized` SET `text`='Ehrenwerter Magistrat Solomon! Ich trauere mit Euch um die Toten. Ich werde mich persönlich darum kümmern, dass der König von der Situation erfährt, und hoffe inständig, Verstärkung nach Seenhain schicken zu können. Doch zeigte sich seine Majestät in letzter Zeit zerstreut und benahm sich etwas merkwürdig. Ich sage Euch dies ganz im Vertrauen. Auf bald General Marcus Jonathan Königreich Sturmwind' WHERE (`entry`='37');
UPDATE `itempages_localized` SET `text`='Stoutmantle -\n\nIhr habt Nerven, uns einen völlig Fremden zu schicken, um die Sache der Defias zu besprechen. Erst rettet Ihr mir das Leben und jetzt bringt Ihr es in Gefahr.\n\nAber Ihr habt etwas gut bei mir. Die Bruderschaft der Defias ist größer als Ihr denkt. Jede Mine zwischen Westfall und dem Wald von Elwynn ist unter ihrer Kontrolle. Kobolde und Gnolle machen inzwischen dort die Drecksarbeit. Goblins erschaffen Monster aus Metall, die in den Feldern von Westfall Jagd auf allzu neugierige Bewohner machen.' WHERE (`entry`='38');
UPDATE `itempages_localized` SET `text`='Sie haben ein riesiges Untergrundnetzwerk aufgebaut. Größer als Ihr erahnen könnt, Stoutmantle. Von Booty Bay bis zur Burg Stormwind. Ihr mögt ein tapferer Paladin sein, aber Ihr seid ein Narr, wenn Ihr glaubt, sie dingfest machen zu können. Sie arbeiten an einer Massenvernichtungswaffe. Wenn sich die Steinmetze schließlich mit etwas auskennen, dann mit Großprojekten. Ihr habt doch nicht wirklich geglaubt, es ginge um Kürbisfarmen und Weinberge, oder? Ich habe Euch den Gefallen zurückgezahlt. Nun lasst mich in Frieden!\n\n- W' WHERE (`entry`='39');
UPDATE `itempages_localized` SET `text`='Sie haben ein riesiges Untergrundnetzwerk aufgebaut. Größer als Ihr erahnen könnt, Stoutmantle. Von Booty Bay bis zur Burg Stormwind. Ihr mögt ein tapferer Paladin sein, aber Ihr seid ein Narr, wenn Ihr glaubt, sie dingfest machen zu können. Sie arbeiten an einer Massenvernichtungswaffe. Wenn sich die Steinmetze schließlich mit etwas auskennen, dann mit Großprojekten. Ihr habt doch nicht wirklich geglaubt, es ginge um Kürbisfarmen und Weinberge, oder? Ich habe Euch den Gefallen zurückgezahlt. Nun lasst mich in Frieden! - W' WHERE (`entry`='40');
###
# 41 - 44
###
UPDATE `itempages_localized` SET `text`='Gut gemacht, Kumpel! Jetzt müsst Ihr Euch imma nach Ost''n halten. Östlich von der Straße die Steilhänge hoch. Schaut Euch nach so ''nem ollen verfallenen Kamin nahe bei der Straße um. Dort findet Ihr''n altes Fass mit Eurem nächsten Hinweis drin.' WHERE (`entry`='45');
UPDATE `itempages_localized` SET `text`='Also, von dem Fass dort müsst Ihr Euch nach Norden halten tun. Un'' dann geht Ihr immer der Nase nach schnurstracks geradeaus, bis Ihr den leeren Henkelkrug sehen tut, der da irgendwo bei so ''ner einzeln stehenden Windmühle oben auf dem Steilhang über dem Meer liegt Wenn Ihr Euch bei dem Henkelkrug umguckt, dann findet Ihr vielleicht sogar, was Ihr sucht.' WHERE (`entry`='46');
UPDATE `itempages_localized` SET `text`='Jetz'', wo Ihr mein'' alten Whiskykrug gefunden habt, habt Ihr den Schatz schon so gut wie in der Tasche. Von der Flasche aus müsst Ihr Euch einfach nach Westen halten und runter zum Strand geh''n. Und dann heißt es schwimmen! Schwimmt schnurstracks nach Westen, bis Ihr an der Insel seid, da wo meine Schatztruhe drauf is''!' WHERE (`entry`='47');
UPDATE `itempages_localized` SET `text`='Schwarznagel -\n\nDas Meer ruft. Wir brauchen von Euch Nachschub aus dem Norden. Schickt uns alles, was Ihr entbehren könnt: jeden Spaten, jeden Wassereimer. Wir brauchen Metall. Wir warten noch auf eine längst überfällige Lieferung von BB. Ich zähle auf Euch, Schwarznagel. Schickt die Sachen diesmal direkt zur "Scheune". Wir haben keine Zeit zu verlieren.\n\n- Van Cleef"' WHERE (`entry`='48');
###
# 49
###
UPDATE `itempages_localized` SET `text`='Euer Ehren!\n\nGanz Westfall betrauert Euren Verlust, aber wir in Westfall haben unseren eigenen Krieg zu führen. Die Bauern wurden von Dieben und mechanischen Golems vertrieben. Sturmwind hat seine Truppen zurückgezogen und das Volk schutzlos zurückgelassen. Ich habe im Zuge von Sturmwinds Rückzug eine Volksmiliz ausgehoben, um zu retten, was von diesem Land noch zu retten ist. Diese Miliz nun in das Rotkammgebirge zu verlegen, würde für Westfall den sicheren Untergang bedeuten.\n\nMit Bedauern,\nGryan Starkmantel' WHERE (`entry`='50');
UPDATE `itempages_localized` SET `text`='Lord Schwarzhaupt,\n\ndie Stadt Seenhain bittet untertänigst um Hilfe durch die Nachtwache. Wir werden von den Orcs des Schwarzfelsklans ebenso belagert wie von Morganths Schattenfellgnollen. Ich würde nicht um Hilfe bitten, wäre die Lage nicht so ernst. Wir erwarten Hilfe aus Sturmwind, aber bis diese eintrifft, flehe ich Euch um Unterstützung an. Wird das Königreich erst überrannt, sind alle Menschen in Gefahr.\n\nMagistrat Solomon' WHERE (`entry`='51');
UPDATE `itempages_localized` SET `text`='Euer Ehren,\n\nich muss Euch leider mitteilen, dass die Nachtwache zurzeit nicht in der Lage ist, ihren Posten in Dunkelhain zu verlassen. Vielleicht war Euch nicht bewusst, dass Sturmwind uns in diesem Gebiet keinerlei militärische Unterstützung mehr zukommen lässt. Die Nachtwache wurde von den Bewohnern des Waldes einzig und allein zu dem Zweck gegründet und ausgebildet, Dunkelhain in Abwesenheit von Sturmwind zu verteidigen. Ohne die Wache würde die Stadt fallen. Ich wünsche Euch alles Glück in Eurer Not.\n\n- Lord Ello Schwarzhaupt' WHERE (`entry`='52');
UPDATE `itempages_localized` SET `text`='Die Buchstaben auf dem Blatt scheinen zu flimmern und zu tanzen... Es ist unmöglich, in ihnen irgendeinen Sinn zu erkennen...' WHERE (`entry`='53');
UPDATE `itempages_localized` SET `text`='Seid gegrüßt, Ello Schwarzhaupt, Bürgermeister von Dunkelhain. Ich habe leider Nachrichten für Euch. Schlechte Nachrichten.\nIch bin ein Schöpfer. Ich habe den bedauernswerten Überbringer dieser Notiz überlistet und zu einem Teil meiner jüngsten Schöpfung gemacht - einem Scheusal aus Fleisch und Knochen und Metall! Während Ihr dies lest, wartet das Wesen wahrscheinlich zähneknirschend draußen vor meinem bescheidenen Heim auf meinen Befehl, um loszuziehen und zu töten.\nAber das erfahrt Ihr noch früh genug.\n- Der Einbalsamierer' WHERE (`entry`='54');
UPDATE `itempages_localized` SET `text`='Dies ist eine Besitzurkunde für ein Stück Land im Gebiet von Westfall. Mit ihr werden unten stehenden Personen die Eigentumsrechte an dem genannten Gebiet sowie an allen innerhalb der Grundstücksgrenzen hergestellten Gütern und errichteten Gebäuden übertragen.\n\nDas Dokument wurde unterzeichnet von:\n\n    Theodore Brauenwirbel\n    Verna Brauenwirbel' WHERE (`entry`='55');
UPDATE `itempages_localized` SET `text`='[Die folgenden Worte wurden hastig auf die Rückseite der Besitzurkunde gekritzelt]\n\nWir haben Brauenwirbel etwas bearbeitet und ihm seine Besitzurkunde abgeluchst. Wäre vielleicht ganz nützlich, wenn Ihr Euch auch so eine fälschen wolltet. Um die Brauenwirbels müssen wir uns jedenfalls keine Sorgen machen. Zuletzt sah ich sie kurz hinter Westfall in einem Wagen mit gebrochener Achse.' WHERE (`entry`='56');
UPDATE `itempages_localized` SET `text`='[Die folgenden Worte wurden hastig auf die Rückseite der Besitzurkunde gekritzelt] Wir haben Brauenwirbel etwas bearbeitet und ihm seine Besitzurkunde abgeluchst. Wäre vielleicht ganz nützlich, wenn Ihr Euch auch so eine fälschen wolltet. Um die Brauenwirbels müssen wir uns jedenfalls keine Sorgen machen. Zuletzt sah ich sie kurz hinter Westfall in einem Wagen mit gebrochener Achse.' WHERE (`entry`='57');
UPDATE `itempages_localized` SET `text`='Der Mond steht überm Tale\nTaucht den Wald in mattes Licht\nWo stolze Krieger den Ruf vernehmen\nZu verteidigen unser Land und heil''gen Grund. \nDer Mond steht überm Tale\nHoch überm Schlachtgetümmel\nWo Blut in Strömen fließt\nUnd Freund wie Feind zu Boden sinkt.' WHERE (`entry`='58');
UPDATE `itempages_localized` SET `text`='Und wenn unsere Brüder einkehren\nIn das Reich jenseits des Bekannten\nHarrt aus der Seelengeist\nTief unter dem Tal.\nUnd wenn unsere Brüder einkehren\nIn den Tempel des Berges\nWerden wir ihren ewigen Geist beschützen\nEingeschlossen in den heiligen blauen Kristall.\nUnd wenn unsere Brüder einkehren\nEin Mond steht überm Tale.' WHERE (`entry`='59');
###
# 60 - 61
###
UPDATE `itempages_localized` SET `text`='Bei Mond und Feuer,\r\nFleisch und Blut,\r\nStört nicht den,\r\nDer hier ruht.\r\n\r\nKehret um\r\nOder seid verflucht\r\nDenn der Tod, er lauert\r\nWo der Imperator ewig ruht.' WHERE (`entry`='62');
UPDATE `itempages_localized` SET `text`='Meister Treuwein,\n $g dieser Bote : diese Botin; hat sich als Wesen des Lichts erwiesen, als tapfer im Kampf gegen die Untoten und Dämonen am Rande des Dämmerwalds und willens, mit Eurer Familie für die große Sache des Lichts zu streiten.\nIch würde daher vorschlagen, Ihr gebt dieser Lichtgestalt weitere Aufgaben - vielleicht die Überwachung einiger Verdächtiger in der Stadt, von denen wir vermuten, dass sie mit den Feinden des Lichts paktieren.\nDas Licht zum Gruße\nCalor' WHERE (`entry`='63');
UPDATE `itempages_localized` SET `text`='Morgan Ladimore war ein großer, edler Ritter. Er kämpfte für die Unschuldigen, Armen und Gepeinigten. Lange Jahre wirkte er eifrig in den abgelegenen Gebieten von Azeroth. Er linderte das Leid der Darbenden und bestrafte die Vergehen der Übeltäter.\n\nIm Sommer seines achtzehnten Jahres heiratete er eine junge Frau namens Lys. Sie liebten sich innig und bekamen drei Kinder, einen Sohn und zwei Töchter.\n\nMorgan war 32, als der Krieg in' WHERE (`entry`='64');
UPDATE `itempages_localized` SET `text`='Lordaeron ausbrach. Morgan wurde zum legendären Paladin Uther Lichtbringer abkommandiert, um an dessen Seite gegen die Orcs und die Untoten zu kämpfen. Morgan zog in den Krieg und glaubte Frau und Kinder zu Hause in Sicherheit.\n\nJahre vergingen und der Krieg zog sich in die Länge. Morgan wurde Zeuge vieler schrecklicher Ereignisse, darunter der Auflösung der Paladine der Silberhand, des Todes von Uther und der Ausbreitung der Seuche. Vor dem Wahnsinn bewahrte ihn in all der Zeit allein das Wissen, dass er eines Tages' WHERE (`entry`='65');
UPDATE `itempages_localized` SET `text`='wieder bei Frau und Kindern sein würde.\n\nMorgan kehrte schließlich tatsächlich in seine Heimat zurück, aber dort war nichts mehr so, wie er es einst gekannt hatte. Die einst grünen Wälder waren jetzt verderbt und wimmelten nur so von Untoten und anderem finsteren Gezücht. Überall sah er zerstörte Häuser und Höfe und der Friedhof von Rabenflucht beherrschte die ganze Gegend. Ein entsetzter, fassungsloser Morgan ritt schließlich eiligst nach Hause zurück, fand dort jedoch nur Ruinen vor. Nicht ahnend, was in seiner Heimat geschehen war,' WHERE (`entry`='66');
UPDATE `itempages_localized` SET `text`='eilte er zum Dorf, wo er nicht nur Antworten zu finden hoffte, sondern auch seine Frau und seine Kinder.\n\nDoch Morgans Nachforschungen waren vergebens. Ein Priester in Dunkelhain, wie der Ort jetzt hieß, sagte, er solle auf dem Friedhof von Rabenflucht nach einem Grabstein suchen. Morgan wollte nicht glauben, dass seine Familie tot war, und setzte seine Suche fort. Kein Haus und keinen Hof im Dämmerwald ließ er aus - es war umsonst.\n\nMorgan ritt von Dunkelhain ins nahe Seenhain, vielleicht war seine Familie ja geflohen. Auf dem Weg' WHERE (`entry`='67');
UPDATE `itempages_localized` SET `text`='dorthin beschloss er wider besseres Wissen, dem Friedhof von Rabenflucht einen Besuch abzustatten. Stundenlang ging Morgan zwischen den Gräbern umher. Er stieß auf zahlreiche Namen von Menschen, die er einst kannte, und tiefe Wehmut überkam ihn. Dann fiel sein Blick auf ein kleines verwahrlostes Grab mit drei kleinen Grabsteinen darauf. Ein Frösteln überlief ihn, als er näher heranging. Morgan wischte den Staub vom ersten Grabstein, um seine Inschrift sichtbar zu machen. Die eingravierten Buchstaben, die zum Vorschein kamen,' WHERE (`entry`='68');
UPDATE `itempages_localized` SET `text`='bestätigten seine schlimmsten Befürchtungen:\n\nLys Ladimore\nGeliebte Ehefrau und Mutter\n\nMorgans Vorahnungen schlugen in Bestürzung um und dann in Kummer. Er fiel weinend auf die Knie. Stunden starrte er auf dieses eine Grab, flehte den kalten Stein um Vergebung an und schluchzte Entschuldigungen. Dann, Stunden später, änderte sich sein Verhalten plötzlich und er begann, um sich zu schlagen. Er zog sein Schwert und hämmerte damit wild auf die Grabsteine ein, wobei er voller Zorn brüllte. Er war blind vor Wut und schlug wild um sich, so dass drei' WHERE (`entry`='69');
UPDATE `itempages_localized` SET `text`='Friedhofsbesucher auf ihn aufmerksam wurden. Als sie ihn zurückzuhalten versuchten, richtete sich seine Wut urplötzlich gegen sie und er beschimpfte die unschuldigen Friedhofsbesucher zuerst, bevor er sie alle tötete.\n\nSpäter, als sein Zorn verflogen war, erkannte Morgan, was er getan hatte: Sein blutiges Schwert steckte in der Brust eines der Friedhofsbesucher. Völlig von seinen Gefühlen übermannt, zog Morgan ein Messer aus seinem Gürtel und rammte es sich selbst ins Herz.\n\nMorgan Ladimores Leichnam und die Leichen seiner drei Opfer wurden' WHERE (`entry`='70');
UPDATE `itempages_localized` SET `text`='am nächsten Tag gefunden. Morgan wurde schnell und ohne jede Zeremonie in einem eiligst ausgehobenen Grab am Rande des Friedhofs beigesetzt. Morgan hatte nicht nur Unschuldige getötet, was seinen Überzeugungen und seiner Natur vollkommen widersprach, er war auch von Kummer überwältigt, weil er seine Familie nicht hatte retten können. Deshalb konnte er nicht in Frieden sterben, sondern musste fortan ein Dasein als Toter führen, der keine Ruhe fand.\n\nWenige Tage später wurde sein Grab geschändet und seine Leiche war unauffindbar. Das Wesen, das einst Morgan gewesen ist,' WHERE (`entry`='71');
UPDATE `itempages_localized` SET `text`='streift nun durch den Dämmerwald, sich in Selbsthass und Gram über den Verlust seiner Familie verzehrend. Mor''Ladim, wie sich das Wesen jetzt nennt, streift, blind vor Hass und Rachsucht, wahllos tötend im Dämmerwald umher.' WHERE (`entry`='72');
UPDATE `itempages_localized` SET `text`='Der Schrecken der vergangenen Wochen würde übersteigen, was ich ertragen kann, könnte ich mir nicht durch das Niederschreiben dessen, was ich gesehen habe, etwas Linderung verschaffen. So schreibe ich denn. Das ist mein einziger Trost in dieser verzweifelten Zeit.' WHERE (`entry`='73');
UPDATE `itempages_localized` SET `text`='TAG 1:\n\nDer Lehrling und ich sind heute im Coldridgetal angekommen. Die Reise durch die Höhle verlief ohne besondere Vorkommnisse. Wir schlugen unser Lager in sicherer Entfernung von der Höhle auf, in der sich die Trolle eingenistet haben sollen.\n\nTAG 2:\n\nDas verdammte Wolfsgeheul ließ mich fast die ganze Nacht kein Auge zutun.\n\nIn ein paar Tagen werde ich wohl neue Kleider aus Wolfsbalg haben.\n\nDer Bursche hat sich heute ausgerechnet mit einem Trogg eingelassen. Es kam zum Kampf und' WHERE (`entry`='74');
###
# 75 - 76
###
UPDATE `itempages_localized` SET `text`='TAG 1: Der Lehrling und ich sind heute im Coldridgetal angekommen. Die Reise durch die Höhle verlief ohne besondere Vorkommnisse. Wir schlugen unser Lager in sicherer Entfernung von der Höhle auf, in der sich die Trolle eingenistet haben sollen. TAG 2: Das verdammte Wolfsgeheul ließ mich fast die ganze Nacht kein Auge zutun. In ein paar Tagen werde ich wohl neue Kleider aus Wolfsbalg haben. Der Bursche hat sich heute ausgerechnet mit einem Trogg eingelassen. Es kam zum Kampf und' WHERE (`entry`='77');
UPDATE `itempages_localized` SET `text`='Dieser Rüstungsgutschein gilt für ein Teil einer Leder- oder Stoffrüstung bei den Roderwalds im Holzfällerlager des Osttals.' WHERE (`entry`='78');
UPDATE `itempages_localized` SET `text`='Dies ist der Ablauf- und Zeitplan, nach dem die Goldsammlung von Defias aus den Minen von Elwynn in unser Hauptquartier nach Westfall gebracht werden soll.\n \nSammelzeitplan:\nSonntag: 12:30 Uhr\nMittwoch: 12:30 Uhr\nAn jedem genannten Tag wird das Gold aus den Minen von Elwynn zunächst bei Brackbrunns Kürbisbeet gesammelt. Der Verantwortliche bei diesen Sammlungen, "Der Sammler", trägt einen Ring mit Gravur. Den Ring hat er von mir.' WHERE (`entry`='79');
UPDATE `itempages_localized` SET `text`='Grelin,\n\nmeine Zeit ist knapp bemessen und ich hoffe, Eure Erforschung der Trolle macht sie nicht noch knapper. Ich gestatte Euch daher, nach Gutdünken mit den Trollen zu verfahren. Solltet Ihr eine zufrieden stellende Lösung finden, umso besser.\n\n\nMagni Bronzebart' WHERE (`entry`='80');
UPDATE `itempages_localized` SET `text`='behielt tatsächlich die Oberhand gegen den hässlichen Kerl, aber dafür hat er jetzt auch eine unangenehme Verletzung am Arm. Also muss ich wohl in den nächsten Tagen die lästigen Pflichten erledigen.\n\nTAG 3:\n\nKalt. Um die Mittagszeit schneite es ein wenig.\n\nTAG 4:\n\nDer Bursche kam gut gelaunt zurück und wir haben uns die Trolle mal genauer angesehen. Nach den Zeichen auf ihrer Haut und dem Plunder an Schmuck zu urteilen, den sie an sich trugen, würde ich sagen: Frostmane.' WHERE (`entry`='81');
UPDATE `itempages_localized` SET `text`='Tag 5:\n\nLieß den Burschen das Lager bewachen und schlich mich in die Höhle, um mich umzusehen. Die Zahl der Trolle ist allmählich Besorgnis erregend. Vielleicht müssen wir noch Gebirgsjäger in großer Zahl anfordern, um die Höhle von den Trollen zu säubern.\n\nMir fällt auf, dass ich zuletzt wenig über die Troggs geschrieben habe. Auch sie kommen in der Region in großer Zahl vor, aber sie sind so primitiv, dass sie wohl keine große Bedrohung darstellen. Es wird sich zeigen, ob das stimmt.' WHERE (`entry`='82');
UPDATE `itempages_localized` SET `text`='Und nicht ein Tropfen Bier seit fast einer Woche.\n\nTAG 6:\n\nWieder kalt. Schnee. Trolle. Und kein Bier.\n\nTAG 7:\n\nDer Schnee so weiß.\nDer Himmel so blau.\nWolfsgeheul bei Nacht.\nUnd noch immer kein Bier.\n\nTAG 8:\n\nDie Trolle sind vielleicht doch nicht so ein großes Problem, wie wir anfangs dachten. Sie sind uns' WHERE (`entry`='83');
UPDATE `itempages_localized` SET `text`='zahlenmäßig überlegen, aber nicht gut bewaffnet oder organisiert. Eine kleine Machtdemonstration sollte genügen, um ihnen ihr Mütchen zu kühlen.' WHERE (`entry`='84');
UPDATE `itempages_localized` SET `text`='Mein Tagebuch\nDer Schrecken der vergangenen Wochen ist so schlimm, dass ich daran zu zerbrechen fürchte. Vielleicht hilft es mir, nicht dem Wahn zu verfallen, indem ich das Unsägliche niederschreibe. Vielleicht ist es so etwas wie eine Beichte gegenüber einem stillen Gefährten oder eine Befreiung meiner Seele von dieser Gedankenpein, indem ich sie auf Papier banne. \nIch habe zuvor bereits einmal ein Tagebuch geführt, doch befindet es sich jetzt an einem Ort, an den ich nicht zurückkehren kann. Also beginne ich von vorn. Aber dieses Mal beginne ich ganz am Anfang.' WHERE (`entry`='85');
UPDATE `itempages_localized` SET `text`='Bericht zur Lage der Frostmähnentrolle im Gebiet des Eisklammtalss\n\nVorgelegt von Grelin Weißbart, Sondergesandter des Senats\n\nNach eingehendem Studium der Bewegungen der Frostmähnentrolle im Gebiet um das Eisklammtal kann ich nun sagen, dass sie für die Zwergensiedlungen in dem Gebiet keine große Bedrohung darstellen. Mit ein wenig mehr Unterstützung durch die Armee können wir dieser Bedrohung leicht Herr werden. Mit Hilfe der bereits im' WHERE (`entry`='86');
UPDATE `itempages_localized` SET `text`='Eisklammtal stationierten Gebirgsjäger und Söldner (die aus Mitteln bezahlt werden, die der Senat vor meiner Entsendung bereitgestellt hat) kann das Problem meiner Überzeugung nach in kurzer Zeit gelöst werden. \n\nIch habe die Genehmigung von König Bronzebart, alle notwendigen Maßnahmen einzuleiten.' WHERE (`entry`='87');
UPDATE `itempages_localized` SET `text`='Bericht zur Lage der Frostmähnentrolle im Gebiet um Dun Morogh\n\nDie in Dun Morogh lebenden Trolle halten sich größtenteils in der Höhle der Frostmähnen auf, einer Berghöhle an der Westgrenze. Rein zahlenmäßig könnten sie eine Bedrohung darstellen, aber sie scheinen mehr als zufrieden damit zu sein, in der Höhle bleiben zu können. Sie wollen zweifellos nicht noch einmal den Zorn der Zwerge heraufbeschwören und damit ihre völlige Ausrottung riskieren. Wenn überhaupt, dann sind die Aktionen der Trolle als Revierstreitigkeiten zu betrachten, und ich bin' WHERE (`entry`='88');
UPDATE `itempages_localized` SET `text`='überzeugt, dass sie für uns keine ernsthafte Bedrohung darstellen, solange wir uns eben nicht in ihr Revier begeben. Dies mag eine für die zwergische Bevölkerung unangenehme Situation sein, aber in Ermangelung militärischer Ressourcen sollten wir die völlige Vernichtung der Trolle vielleicht einstweilen zurückstellen und uns vielmehr auf die Bedrohung durch die Troggs und die Dunkeleisenzwerge konzentrieren.\n\nAnbei auch ein Bericht meines Bruders Grelin über Ambossar.\n\nGezeichnet\nSenir Weißbart' WHERE (`entry`='89');
UPDATE `itempages_localized` SET `text`='Bericht zur Lage der Frostmähnentrolle im Gebiet des Eisklammstals\n\nVorgelegt von Grelin Weißbart, Sondergesandter des Senats\n\nNach eingehendem Studium der Bewegungen der Frostmähnentrolle im Gebiet um das Eisklammtal kann ich nun sagen, dass sie für die Zwergensiedlungen in dem Gebiet keine große Bedrohung darstellen. Mit ein wenig mehr Unterstützung durch die Armee können wir dieser Bedrohung leicht Herr werden. Mit Hilfe der bereits im' WHERE (`entry`='90');
UPDATE `itempages_localized` SET `text`='Eisklammtal stationierten Gebirgsjäger und Söldner (die aus Mitteln bezahlt werden, die der Senat vor meiner Entsendung bereitgestellt hat) kann das Problem meiner Überzeugung nach in kurzer Zeit gelöst werden. \n\nIch habe die Genehmigung von König Bronzebart, alle notwendigen Maßnahmen einzuleiten.' WHERE (`entry`='91');
UPDATE `itempages_localized` SET `text`='Mein lieber Tarrel -\nverzeiht die Hast, mit der diese Zeilen geschrieben sind, aber Zeit ist ein kostbares Gut, das wir uns nicht leisten können. \nDie Grabungsmannschaft hatte bei der Bergung der Titanenartefakte große Fortschritte gemacht. Aber dann stießen wir auch auf andere alte Objekte, insbesondere riesige Knochen.' WHERE (`entry`='92');
UPDATE `itempages_localized` SET `text`='Kaum hatten wir die Ersten dieser Knochen gehoben, als wir von Raptoren angegriffen wurden. Das Bataillon aus Langzopfs Regiment kämpfte tapfer. Aber letztlich wurde es überwältigt. Fast alle kamen dabei um. Nur drei von uns überlebten. Ein Hilfsarbeiter namens Ormer Eisenzopf erwies sich dabei als äußerst heldenhaft, denn er rettete sowohl mich als auch Ausgrabungsleiter Whelgar.' WHERE (`entry`='93');
UPDATE `itempages_localized` SET `text`='Wir haben uns einstweilen in eine geschützte Höhle geflüchtet, wo wir wohl in Sicherheit sind. Aber wir sitzen fest, bis Truppen eintreffen, die sich der Raptoren annehmen. Whelgar versucht, die Arbeit fortzusetzen, aber ich befürchte, er hat noch immer recht große Angst. So viel zu unserer Situation, Tarrel. Bitte drängt bei Langzopf auf die sofortige Entsendung eines Rettungstrupps.\nMeine Liebe zu Euch ist auch in diesen schweren Zeiten unvermindert groß.\n- Merrin' WHERE (`entry`='94');
UPDATE `itempages_localized` SET `text`='Die Grünen Hügel des Schlingendorntals\nDafür, dass es unser erster Tag war, lief es heute eigentlich erstaunlich gut. Wir verbrachten einen Großteil der Zeit mit den Vorbereitungen für den Bau eines Stützpunkts. Ich hatte den idealen Ort dafür am Frischwasserzulauf eines Flusses ausgemacht. Den alten verlassenen Docks nach zu urteilen war dieser Ort sogar einmal bewohnt gewesen. Doch die ursprünglichen Bewohner sind längst Geschichte.' WHERE (`entry`='95');
###
# 96 - 97
###
UPDATE `itempages_localized` SET `text`='In den Jahren nach dem Verrat Sturmwinds an der Steinmetzgilde erfüllte die Rolle, die der Adel im Königreich spielte, Sire Erlgadin zunehmend mit Gram. Er wollte die Stellung, die er von seinem Vater im Oberhaus geerbt hatte, nicht länger wahrnehmen.\nAber ich schweife ab. Dies hier soll ja keine politische Abhandlung oder Biografie werden. Vielmehr geht es hier um meine Erfahrungen mit der Großwildjagd in den grünen Hügeln des Schlingendorntals.' WHERE (`entry`='98');
UPDATE `itempages_localized` SET `text`='In den Jahren nach dem Verrat Stormwinds an der Steinmetzgilde erfüllte die Rolle, die der Adel im Königreich spielte, Sire Erlgadin zunehmend mit Gram. Er wollte die Stellung, die er von seinem Vater im Oberhaus geerbt hatte, nicht länger wahrnehmen. Aber ich schweife ab. Dies hier soll ja keine politische Abhandlung oder Biografie werden. Vielmehr geht es hier um meine Erfahrungen mit der Großwildjagd in den grünen Hügeln des Schlingendorntals.' WHERE (`entry`='99');
UPDATE `itempages_localized` SET `text`='Gerade als ich fragen wollte, ob Ajeck denn nicht an der heutigen Jagdstrategie interessiert sei, griff sie nach ihrem Köcher, zog einen Pfeil heraus und schoss ihn just auf unseren armen Barnil ab. Aber der Schuss galt gar nicht Barnil. Denn als der offenen Mundes einen Schritt zur Seite machte, trieb ein großer Flusskrokilisk an die Oberfläche. Ajecks Pfeil steckte genau zwischen seinen großen Augen.' WHERE (`entry`='100');
###
# 101
###
UPDATE `itempages_localized` SET `text`='Nach einem solchen Fehltritt legte Erlgadin Barnil die Hand schwer auf die Schulter. Ajeck und ich schauten beiläufig. Wir dachten, Erlgadin würde Barnil ob seiner Unvorsichtigkeit endlich einmal die Leviten lesen. Aber Erlgadin wies mit einer langsamen Kopfbewegung in Richtung eines umgestürzten Baumes ganz in der Nähe. Zwei stechend schwarze Augen über einem Maul voller rasiermesserscharfer Zähne starrten uns an.' WHERE (`entry`='102');
###
# 103
###
UPDATE `itempages_localized` SET `text`='Danach waren wir alle in Hochstimmung. Barnin gab eine Runde Met für alle aus. Aber die Freude sollte nicht lange währen. Denn als wir den Kadaver für den Rücktransport zum Stützpunkt fertig machten, hörten wir unvermittelt ein fürchterliches Heulen. In all den Jahren habe ich noch nie etwas so Schreckliches gehört.' WHERE (`entry`='104');
UPDATE `itempages_localized` SET `text`='Auf einer felsigen Anhöhe über uns hob sich gegen das Licht der untergehenden Sonne die Silhouette der größten Raubkatze ab, die ich je gesehen habe. Ich gab einen eher unbeholfenen Schuss in Richtung des Tiers ab, das sich davon aber unbeeindruckt zeigte. Die Raubkatze ließ erneut ein Knurren ertönen, lauter noch als beim ersten Mal, und verschwand.\nWir packten unsere Sachen und kehrten gut gelaunt ins Lager zurück.' WHERE (`entry`='105');
###
# 106 - 107
###
UPDATE `itempages_localized` SET `text`='Wir hielten uns nach Süden und folgten einigen frischen Pantherspuren. Kurz darauf kamen wir an eine Klamm, die von einer phantastischen Hängebrücke überspannt wurde. Ich musste sofort an Branns eindrucksvolle Beschreibungen des Gebiets denken, als ich dieses technische Wunderwerk sah. Es wurde oft behauptet, die einheimischen Trolle seien primitiv und ungebildet, doch als ich auf dieses handwerkliche Meisterwerk blickte, erkannte ich mit einem Mal das außerordentliche Können, mit dem die Erbauer der Trolle das schier unüberwindliche Hindernis überwunden hatten.' WHERE (`entry`='108');
UPDATE `itempages_localized` SET `text`='Wir hielten uns nach Süden und folgten einigen frischen Pantherspuren. Kurz darauf kamen wir an eine Klamm, die von einer phantastischen Hängebrücke überspannt wurde. Ich musste sofort an Branns eindrucksvolle Beschreibungen des Gebiets denken, als ich dieses technische Wunderwerk sah. Es wurde oft behauptet, die einheimischen Trolle seien primitiv und ungebildet, doch als ich auf dieses handwerkliche Meisterwerk blickte, erkannte ich mit einem Mal das außerordentliche Können, mit dem die Erbauer der Trolle das schier unüberwindliche Hindernis überwunden hatten.' WHERE (`entry`='109');
UPDATE `itempages_localized` SET `text`='Ajeck und Sire Erlgadin standen unbeweglich und hielten ihre Flinten auf das dichte Buschwerk unter den sanft schwingenden Bäumen gerichtet. Die Mittagssonne brannte auf uns nieder. Ein kleiner Schweißtropfen rann an Erlgadins Schläfe herab, als er den Hahn spannte. In dem Moment, als der Hahn einrastete, teilte sich mit einem Mal das Dickicht und ein großer schwarzer Panther - ein schönes Exemplar - preschte hervor.' WHERE (`entry`='110');
UPDATE `itempages_localized` SET `text`='Die Büchse ruckte heftig zurück. Er verriss den Schuss seitwärts und geriet mit dem Lauf just unter Ajecks Flinte. Ajeck ihrerseits hatte genau diesen Moment abgepasst, um abzudrücken. Der Schuss verirrte sich irgendwo in die Bäume. Ein Schwarm Vögel flog kreischend aus dem Blätterdach auf und flatterte aufgeregt in alle Himmelsrichtungen davon. Eine Wolke Pulverdampf stieg über einem Baum auf. Fassungslos sahen wir mit an, wie ein gewaltiger Ast geradewegs auf den flüchtenden Panther fiel und ihm das Rückgrat brach.' WHERE (`entry`='112');
UPDATE `itempages_localized` SET `text`='Wir machten uns beim ersten Tageslicht in Richtung Süden zu den Tkashi-Ruinen auf. Barnil äußerte sich besorgt, dass wir auf Mitglieder des Blutskalpstamms treffen könnten. Ich erinnerte ihn daran, dass die Blutskalpe mehr mit der Vernichtung des verfeindeten Schädelspalterstamms beschäftigt waren. Ich brauche wohl nicht zu betonen, dass Barnil alles andere als beruhigt war. Ich für mein Teil vertraute jedoch auf meine geladene Flinte, einen Ranzen Schießpulver und die drei versierten Jäger neben mir und machte mir keine Gedanken über einen möglichen Hinterhalt.' WHERE (`entry`='114');
UPDATE `itempages_localized` SET `text`='Ich stand vor einer riesigen Höllenbestie auf dem Schlachtfeld und die Armee der Brennenden Legion näherte sich von allen Seiten. Dagegen wirkt eine Bande Trolle so harmlos wie ein Kaninchen in den Hügeln von Dun Morogh.\nWir passierten die Tkashi-Ruinen ohne Zwischenfall, worüber insbesondere Barnil sehr erleichtert war. Die Gruppe ging weiter westwärts Richtung Großes Meer und streifte die Ruinen von Zul''Kunda im Süden. Als wir die Steilküste erklommen, sahen wir ihn: unseren ersten Raptor.' WHERE (`entry`='115');
UPDATE `itempages_localized` SET `text`='Unsere hastig abgefeuerten Schüsse ermöglichten Barnil die Flucht. Barnil stolperte den Hügel wieder hinunter und schloss sich uns erneut an. Wir schlugen uns in den Urwald, wobei das Rudel Schmetterschwanzraptoren jede unserer Bewegungen genau verfolgte.\nAus Jägern waren Gejagte geworden.' WHERE (`entry`='118');
UPDATE `itempages_localized` SET `text`='Ich führte die Gruppe in Richtung Meer und hoffte, dass wir an der Küste Schutz vor den Raptoren finden würden. Doch in unserer Hast waren wir zu weit nach Norden abgekommen und befanden uns auf einer gefährlich hohen Erhebung. Das war ein Fehler. Und ich war schuld. Plötzlich standen wir vor steil abfallenden Klippen, die Raptoren nur noch ein paar Schritte hinter uns.' WHERE (`entry`='119');
UPDATE `itempages_localized` SET `text`='Ich trat langsam mit erhobener Flinte vor. Ich hatte diese mutigen Jäger ins Verderben geführt. Also wollte ich auch bei ihrer Verteidigung sterben. Schmetterschwanzraptoren sind besonders angriffslustig und bekannt für ihren unstillbaren Blutdurst. Zudem waren sie uns zahlenmäßig weit überlegen. Doch ich wollte verdammt sein, wenn sie mich und meine Kameraden töten könnten, ohne dabei selbst Verluste zu erleiden.' WHERE (`entry`='120');
UPDATE `itempages_localized` SET `text`='Ajeck und Sire Erlgadin machten ihre Waffen bereit und gaben mir seitlich Deckung, während wir mit dem Rücken zum Wasser standen. Barnil seufzte ergeben und zog seine Axt. Die Schmetterschwanzraptoren waren schon ganz nah. Sie wurden langsamer. Sie pirschten sich an ihre Beute heran, wussten sie doch, dass wir ihnen nicht mehr entkommen konnten.' WHERE (`entry`='121');
###
# 122 - 152
###
UPDATE `itempages_localized` SET `text`='Dafür, dass es unser erster Tag war, lief es heute eigentlich erstaunlich gut. Wir verbrachten einen Großteil der Zeit mit den Vorbereitungen für den Bau eines Basislagers. Ich hatte den idealen Ort dafür am Frischwasserzulauf eines Flusses ausgemacht. Den alten verlassenen Docks nach zu urteilen, war dieser Ort sogar einmal bewohnt gewesen. Doch die einstigen Bewohner sind längst Vergangenheit.' WHERE (`entry`='153');
UPDATE `itempages_localized` SET `text`='Für diese Expedition habe ich Ajeck Rouack und Sire S. J. Erlgadin um mich geschart. Zudem steht mir mein getreuer Diener Barnil Steinkrug zur Seite. Ich habe an der Seite von Ajecks Vater gar manche Schlacht zur Verteidigung der Allianz gefochten. Sie jetzt erwachsen zu sehen, berührt mich eigentümlich. Ihr Vater hat sie gut in der Waffenkunst unterrichtet. Sie geht so geschickt mit dem Bogen um, dass ich mich frage, ob Elfenblut in ihren Adern fließt.' WHERE (`entry`='154');
UPDATE `itempages_localized` SET `text`='Sire S. J. Erlgadin entstammt dem menschlichen Adel. Sein Vater war der für seine Großzügigkeit bekannte Graf Erlgadin. Der Graf war es, der sich während des Wiederaufbaus von Sturmwind nach dem Zweiten Großen Krieg für bessere Arbeitsbedingungen für die Steinmetzgilde einsetzte.' WHERE (`entry`='155');
UPDATE `itempages_localized` SET `text`='In den Jahren nach dem Verrat Sturmwinds an der Steinmetzgilde erfüllte die Rolle, die der Adel im Königreich spielte, Sire Erlgadin zunehmend mit Gram. Er wollte die Stellung, die er von seinem Vater im Oberhaus geerbt hatte, nicht länger wahrnehmen. Aber ich schweife ab. Dies hier soll ja keine politische Abhandlung oder Biografie werden. Vielmehr geht es hier um meine Erfahrungen mit der Großwildjagd in den grünen Hügeln des Schlingendorntals.' WHERE (`entry`='156');
UPDATE `itempages_localized` SET `text`='Wir standen beim ersten Sonnenstrahl auf. Barnil machte das Frühstück. Mir fiel auf, dass Ajeck irgendwie irritiert wirkte. Wir hatten einen langen Weg vor uns und unsere Jagd versprach, ziemlich gefährlich zu werden. Eine einzige Unachtsamkeit konnte schlimme Folgen haben. Doch Ajeck konnte ihren Blick anscheinend nicht von Barnil wenden, der am Flussufer ein paar Sachen abwusch.' WHERE (`entry`='157');
UPDATE `itempages_localized` SET `text`='Gerade als ich fragen wollte, ob Ajeck denn nicht an der heutigen Jagdstrategie interessiert sei, griff sie nach ihrem Köcher, zog einen Pfeil heraus und schoss ihn just auf unseren armen Barnil ab. Aber der Schuss galt gar nicht Barnil. Denn als der offenen Mundes einen Schritt zur Seite machte, trieb ein großer Flusskrokilisk an die Oberfläche. Ajecks Pfeil steckte genau zwischen seinen großen Augen.' WHERE (`entry`='158');
UPDATE `itempages_localized` SET `text`='Wir gingen Richtung Westen durch das Dickicht des Urwalds. Langsam und vorsichtig bahnten wir uns auf der Suche nach Beute unseren Weg durch das Unterholz. Der Morgen verging und nichts passierte. Nicht einmal ein Lufthauch regte sich im Tal. Bis zum Nachmittag hatte sich Unruhe in unserer Expedition breit gemacht. Barnil ließ es an der für einen Jäger so wichtigen Vorsicht mangeln. Er stapfte ziemlich ungeschickt vor sich hin und trat oft auf trockenes Laub oder herabgefallene Zweige.' WHERE (`entry`='159');
UPDATE `itempages_localized` SET `text`='Nach einem solchen Fehltritt legte Erlgadin Barnil die Hand schwer auf die Schulter. Ajeck und ich schauten beiläufig. Wir dachten, Erlgadin würde Barnil ob seiner Unvorsichtigkeit endlich einmal die Leviten lesen. Aber Erlgadin wies mit einer langsamen Kopfbewegung in Richtung eines umgestürzten Baumes ganz in der Nähe. Zwei stechend schwarze Augen über einem Maul voller rasiermesserscharfer Zähne starrten uns an.' WHERE (`entry`='160');
UPDATE `itempages_localized` SET `text`='Es handelte sich um einen männlichen Schlingendorntiger. Ehe ich auch nur nach meiner Büchse greifen konnte, hatte Erlgadin schon einen Armbrustbolzen abgefeuert. Der Bolzen verfehlte sein Ziel und traf den Tiger nur in der linken Flanke. Er versuchte zu fliehen, aber die Wunde war zu schwer. Das Wildtier taumelte noch einige Sekunden angeschlagen herum, ehe Barnil ihm mit seiner Wurfaxt den Gnadenstoß versetzte.' WHERE (`entry`='161');
UPDATE `itempages_localized` SET `text`='Danach waren wir alle in Hochstimmung. Barnin gab eine Runde Met für alle aus. Aber die Freude sollte nicht lange währen. Denn als wir den Kadaver für den Rücktransport zum Stützpunkt fertig machten, hörten wir unvermittelt ein fürchterliches Heulen. In all den Jahren habe ich noch nie etwas so Schreckliches gehört.' WHERE (`entry`='162');
UPDATE `itempages_localized` SET `text`='Auf einer felsigen Anhöhe über uns hob sich gegen das Licht der untergehenden Sonne die Silhouette der größten Raubkatze ab, die ich je gesehen habe. Ich gab einen eher unbeholfenen Schuss in Richtung des Tiers ab, das sich davon aber unbeeindruckt zeigte. Die Raubkatze ließ erneut ein Knurren ertönen, lauter noch als beim ersten Mal, und verschwand.\nWir packten unsere Sachen und kehrten gut gelaunt ins Lager zurück.' WHERE (`entry`='163');
UPDATE `itempages_localized` SET `text`='Ich hatte meinen Begleitern versprochen, dass wir am nächsten Tag Panther jagen würden, da deren Felle in Azeroth sehr gefragt sind. Diese Nachfrage lässt sich nur damit erklären, dass all die fähigen Jäger, Fallensteller und Pelzhändler fort sind und ihr Leben im Namen der Allianz lassen.' WHERE (`entry`='164');
UPDATE `itempages_localized` SET `text`='Ajeck und Sire Erlgadin waren ganz erpicht darauf zu lernen, wie man Zwergengewehre einsetzt. Daher hatte ich die beiden Menschen angewiesen, ihre primitiven Distanzwaffen im Stützpunkt zu lassen. Barnil und ich statteten sie also mit den besten Feuerwaffen aus Eisenschmiede aus.' WHERE (`entry`='165');
UPDATE `itempages_localized` SET `text`='Wir hielten uns nach Süden und folgten einigen frischen Pantherspuren. Kurz darauf kamen wir an eine Klamm, die von einer phantastischen Hängebrücke überspannt wurde. Ich musste sofort an Branns eindrucksvolle Beschreibungen des Gebiets denken, als ich dieses technische Wunderwerk sah. Es wurde oft behauptet, die einheimischen Trolle seien primitiv und ungebildet, doch als ich auf dieses handwerkliche Meisterwerk blickte, erkannte ich mit einem Mal das außerordentliche Können, mit dem die Erbauer der Trolle das schier unüberwindliche Hindernis überwunden hatten.' WHERE (`entry`='166');
UPDATE `itempages_localized` SET `text`='Bald darauf spürte Ajeck im Südwesten den Panther auf. Leise schlichen wir weiter, die Flinten im Anschlag. Das Knacken von Zweigen im Unterholz ließ uns aufhorchen. Da war etwas. Ein Blick genügte, um mich mit Barnil zu verständigen. Er nahm langsam die Flinte herunter. Dieser Abschuss war nicht für uns bestimmt; er sollte unseren menschlichen Freunden gehören. Unzählige Panther waren schon von unseren Flinten zur Strecke gebracht worden. Dieser hier würde von den Menschen erlegt.' WHERE (`entry`='167');
UPDATE `itempages_localized` SET `text`='Ajeck und Sire Erlgadin standen unbeweglich und hielten ihre Flinten auf das dichte Buschwerk unter den sanft schwingenden Bäumen gerichtet. Die Mittagssonne brannte auf uns nieder. Ein kleiner Schweißtropfen rann an Erlgadins Schläfe herab, als er den Hahn spannte. In dem Moment, als der Hahn einrastete, teilte sich mit einem Mal das Dickicht und ein großer schwarzer Panther - ein schönes Exemplar - preschte hervor.' WHERE (`entry`='168');
UPDATE `itempages_localized` SET `text`='Die Menschen nahmen den Panther aufs Korn, während dieser am Waldrand entlanglief. Die Läufe ihrer Flinten bewegten sich absolut synchron. Barnil warf mir einen drängenden Blick zu, aber ich schüttelte verneinend den Kopf. Diese Jagd war für die Menschen und nicht für Barnil und mich. Erlgadin feuerte, doch er verfehlte den Panther. Auf den starken Rückstoß des Gewehrs war er nicht gefasst.' WHERE (`entry`='169');
UPDATE `itempages_localized` SET `text`='Die Büchse ruckte heftig zurück. Er verriss den Schuss seitwärts und geriet mit dem Lauf just unter Ajecks Flinte. Ajeck ihrerseits hatte genau diesen Moment abgepasst, um abzudrücken. Der Schuss verirrte sich irgendwo in die Bäume. Ein Schwarm Vögel flog kreischend aus dem Blätterdach auf und flatterte aufgeregt in alle Himmelsrichtungen davon. Eine Wolke Pulverdampf stieg über einem Baum auf. Fassungslos sahen wir mit an, wie ein gewaltiger Ast geradewegs auf den flüchtenden Panther fiel und ihm das Rückgrat brach.' WHERE (`entry`='170');
UPDATE `itempages_localized` SET `text`='Im Laufe der Wochen vergrößerte sich unser Bestand an Panther- und Tigerfellen immens. Daher entschied ich, dass wir uns einem neuen Ziel zuwenden sollten: Raptoren.\nSo dankbar die Menschen auch für die Schießübungen waren, die Barnil und ich mit ihnen machten, sie wollten doch von der Benutzung der Feuerwaffen Abstand nehmen. Ajeck kam mit ihrem fein gearbeiteten Bogen weit besser zurecht und Sire Erlgadin verließ das Lager ohnehin nie ohne seine stabile Armbrust.' WHERE (`entry`='171');
UPDATE `itempages_localized` SET `text`='Wir machten uns beim ersten Tageslicht in Richtung Süden zu den Tkashi-Ruinen auf. Barnil äußerte sich besorgt, dass wir auf Mitglieder des Blutskalpstamms treffen könnten. Ich erinnerte ihn daran, dass die Blutskalpe mehr mit der Vernichtung des verfeindeten Schädelspalterstamms beschäftigt waren. Ich brauche wohl nicht zu betonen, dass Barnil alles andere als beruhigt war. Ich für mein Teil vertraute jedoch auf meine geladene Flinte, einen Ranzen Schießpulver und die drei versierten Jäger neben mir und machte mir keine Gedanken über einen möglichen Hinterhalt.' WHERE (`entry`='172');
UPDATE `itempages_localized` SET `text`='Ich stand vor einer riesigen Höllenbestie auf dem Schlachtfeld und die Armee der Brennenden Legion näherte sich von allen Seiten. Dagegen wirkt eine Bande Trolle so harmlos wie ein Kaninchen in den Hügeln von Dun Morogh.\nWir passierten die Tkashi-Ruinen ohne Zwischenfall, worüber insbesondere Barnil sehr erleichtert war. Die Gruppe ging weiter westwärts Richtung Großes Meer und streifte die Ruinen von Zul''Kunda im Süden. Als wir die Steilküste erklommen, sahen wir ihn: unseren ersten Raptor.' WHERE (`entry`='173');
UPDATE `itempages_localized` SET `text`='Das Tier nahm uns noch nicht einmal wahr. Das Einzige, was es von der Expedition mitbekam, war eine Kugel zwischen seinen Augen. Sire Erlgadin ließ ein herzliches "Hurra!" hören und Ajeck nickte mir anerkennend zu. Ich kramte in meiner Tasche, weil ich mir zur Feier des Tages ein Pfeifchen gönnen wollte. Barnil kletterte die Anhöhe hinauf, um den Kadaver des Raptors zu holen. Äußerst zufrieden betrachtete ich das tote Vieh.' WHERE (`entry`='174');
UPDATE `itempages_localized` SET `text`='Die Freude über den Erfolg währte jedoch nicht lange. Denn als ich zum Horizont blickte, sah ich auf dem Hügel genau über dem armen Barnil gleich mehrere Silhouetten. \n"Lauf, Barnil!", schrie ich. Ajeck, Sire Erlgadin und ich feuerten aus allen Rohren auf die heranstürmenden Raptoren und deckten sie mit Kugeln, Pfeilen und Bolzen ein. Einer der Raptoren ging tödlich getroffen zu Boden.' WHERE (`entry`='175');
UPDATE `itempages_localized` SET `text`='Unsere hastig abgefeuerten Schüsse ermöglichten Barnil die Flucht. Barnil stolperte den Hügel wieder hinunter und schloss sich uns erneut an. Wir schlugen uns in den Urwald, wobei das Rudel Schmetterschwanzraptoren jede unserer Bewegungen genau verfolgte.\nAus Jägern waren Gejagte geworden.' WHERE (`entry`='176');
UPDATE `itempages_localized` SET `text`='Ich führte die Gruppe in Richtung Meer und hoffte, dass wir an der Küste Schutz vor den Raptoren finden würden. Doch in unserer Hast waren wir zu weit nach Norden abgekommen und befanden uns auf einer gefährlich hohen Erhebung. Das war ein Fehler. Und ich war schuld. Plötzlich standen wir vor steil abfallenden Klippen, die Raptoren nur noch ein paar Schritte hinter uns.' WHERE (`entry`='177');
UPDATE `itempages_localized` SET `text`='Ich trat langsam mit erhobener Flinte vor. Ich hatte diese mutigen Jäger ins Verderben geführt. Also wollte ich auch bei ihrer Verteidigung sterben. Schmetterschwanzraptoren sind besonders angriffslustig und bekannt für ihren unstillbaren Blutdurst. Zudem waren sie uns zahlenmäßig weit überlegen. Doch ich wollte verdammt sein, wenn sie mich und meine Kameraden töten könnten, ohne dabei selbst Verluste zu erleiden.' WHERE (`entry`='178');
UPDATE `itempages_localized` SET `text`='Ajeck und Sire Erlgadin machten ihre Waffen bereit und gaben mir seitlich Deckung, während wir mit dem Rücken zum Wasser standen. Barnil seufzte ergeben und zog seine Axt. Die Schmetterschwanzraptoren waren schon ganz nah. Sie wurden langsamer. Sie pirschten sich an ihre Beute heran, wussten sie doch, dass wir ihnen nicht mehr entkommen konnten.' WHERE (`entry`='179');
UPDATE `itempages_localized` SET `text`='Da geschah das Unglaubliche: Neben uns hörten wir plötzlich das erschreckende Gebrüll des großen weißen Tigers. Augenblicklich drehten die Raptoren ab und stoben in alle Richtungen davon. Wir sahen nur einen weißen Blitz, als der Tiger an uns vorbeischoss und sich auf einen der Raptoren stürzte. Befehle waren unnötig. Wir alle vier wussten, dass es galt, die Beine in die Hand zu nehmen.' WHERE (`entry`='180');
UPDATE `itempages_localized` SET `text`='Wir rannten so schnell es ging und ohne Pause zurück zum Stützpunkt. Abends saßen wir noch lange still ums Lagerfeuer herum, wussten wir doch nur zu gut, dass wir unser Leben allein einer merkwürdigen Wendung des Schicksals zu verdanken hatten. Das sind die Risiken eines Großwildjägers. Man spielt mit dem Schicksal, wenn man es herausfordert. Und doch bekommt jeder von uns irgendwann die messerscharfen Zähne seines Schicksals zu spüren. Dieser Zwerg hier ist einfach nur froh, dass dieser Augenblick ihn nicht auf den grünen Hügeln des Schlingendorntals ereilt hat.' WHERE (`entry`='181');
UPDATE `itempages_localized` SET `text`='Ehrenwerte Kollegen und Wahrheitssucher,\ndie Ausgrabungen an dieser Stätte schreiten weiter voran, werden jedoch durch das Auftauchen der bereits in meinem letzten Bericht erwähnten Troggs behindert. Ich bin zuversichtlich, dass wir mit diesem Problem fertig werden, auch wenn einige dieser Troggs aggressiver sind als gewöhnlich.\nEs wurden jüngst weitere Artefakte gefunden, aber mit einer Ausnahme (zu der ich später noch Stellung nehmen werde) fallen sie alle in dieselbe Kategorie wie die anderen zuvor: interessant, aber keinesfalls eine Sensation.' WHERE (`entry`='182');
UPDATE `itempages_localized` SET `text`='Eine Ausnahme gab es jedoch. Diese kürzlich entdeckten merkwürdig geschnitzten Götzen (die ich in meinem letzten Bericht bereits einmal kurz beschrieben habe) scheinen eine gewisse Wirkung auf die Troggs an der Ausgrabungsstätte zu haben. Sie werden von den Steinschnitzereien wie magisch angezogen und mancher Trogg ist danach schon Amok gelaufen!\nUm Rückschlüsse auf diese Götzen ziehen zu können, sind weitere Untersuchungen notwendig, aber ich habe die Hoffnung, dass wir etwas über die Verbindung zwischen Troggs und Titanen erfahren können.' WHERE (`entry`='183');
UPDATE `itempages_localized` SET `text`='Zum Schluss muss ich noch einmal meine Bitte um Sprengpulver wiederholen. Meine Bestände gehen bedrohlich zur Neige und das beeinträchtigt den Erfolg der Ausgrabung nachhaltig. Mir wurde gesagt, eine Ladung Sprengpulver sei auf dem Weg, aber sie ist bislang noch nicht angekommen.\nDarf ich den Grund für diese Verzögerung erfahren?\nHochachtungsvoll\nAusgrabungsleiter Darteus Eisenband' WHERE (`entry`='184');
UPDATE `itempages_localized` SET `text`='Alles begann mit dem Fund dieser verfluchten Sense in Rolands Verdammnis. Davor war dies hier ein ebenso friedlicher Ort gewesen wie etwa das Nordhaintal.\nAber seit ich den Griff dieser verdammten Sense aus dem Geröll in der Mine ragen sah und es prompt nicht lassen konnte, sie - verflucht sei ich! - auch noch herauszuziehen, ist Rolands Verdammnis ein Ort des Todes!' WHERE (`entry`='185');
UPDATE `itempages_localized` SET `text`='Alles begann mit dem Fund dieser verfluchten Sense in der Mine mit dem bezeichnenden Namen Rolands Verdammnis. Ja, damit fing alles an. Davor war die Bruderschaft der Defias mit unserem Vorankommen im Dämmerwald ganz zufrieden. In der Zeit vor der Sense war dies hier ein ebenso friedlicher Ort wie etwa das Nordhaintal.\nAber seit ich den Griff dieser verdammten Sense aus dem Schutthaufen in der Mine ragen sah und es prompt nicht lassen konnte, sie - verflucht sei ich! - auch noch herauszuziehen, ist Rolands Verdammnis ein Ort des Todes!' WHERE (`entry`='186');
UPDATE `itempages_localized` SET `text`='Hätte ich geahnt, was passieren würde, ich hätte mir lieber die rechte Hand abgehackt, als dieses runenverzierte Holz anzufassen! So viel Gejammer! Bislang hielt ich das immer für ein Vorrecht der Alten. Jetzt weiß ich, dass es nicht die Alten sind - die Hoffnungslosen sind es, die den Mantel des Bedauerns tragen müssen, weil sie ihn nicht von ihren gramgebeugten Schultern streifen können oder wollen.\nAber Schluss jetzt mit diesem traurig triefenden Tiefsinn! Ich muss mit der Chronik weitermachen...' WHERE (`entry`='187');
UPDATE `itempages_localized` SET `text`='Nachdem ich die Sense herausgezogen hatte, schwappte eine Welle der Veränderung durch die Mine. Das Licht unserer flackernden Fackeln verzerrte sich und wir schienen die Lautstärke unsere Stimmen nicht mehr unter Kontrolle zu haben. Manchmal donnerte das Flüstern eines Mannes so laut durch die Gänge, dass wir uns die Ohren zuhalten mussten, und dann wieder hörte man laute Rufe keine zwei Schritte weit, bevor sie wie ein Hauch vom Wind davongetragen wurden.\nDas raubte uns die Nerven, aber wir hatten ohnehin nicht viel Zeit uns darüber aufzuregen. Es sollte nur der Vorbote dessen sein, was uns wirklich aus der Mine jagte: die Worgen.' WHERE (`entry`='188');
UPDATE `itempages_localized` SET `text`='Sie stürzten sich von allen Seiten auf uns, kamen aus versteckten Löchern zu unseren Füßen oder ließen sich von oben lautlos auf uns herabfallen. Die Hälfte der Männer fiel schon in diesen ersten panikerfüllten Minuten. Der Rest - darunter auch ich - versuchte zu fliehen. Ich rannte und hörte von allen Seiten, wie meine Brüder von Klauen und Zähne zerrissen wurden. Ich hörte unzählige Schreie, die abrupt verstummten oder in einem Gurgeln erstarben.\nNach allem, was ich weiß, bin ich der einzige Mensch, dem die Flucht von diesem Ort gelang.' WHERE (`entry`='189');
UPDATE `itempages_localized` SET `text`='Ich kann nur raten, warum ich diese Nacht überlebt habe. Ich war immer schon vorsichtig, bin Schlägen ausgewichen und habe einen Bogen um Fallgruben gemacht. Daher kommt auch mein Spitzname Bibbers. Vielleicht war es also dieser Hang zur Vorsicht, der mich gerettet hat...\nOder es war tatsächlich diese Sense, die ich aus dem Geröll gezogen habe. An der Sense selbst kann es indes nicht liegen, denn die habe ich während meiner panischen Flucht verloren. Aber wenn ich es war, der die Worgen in den Dämmerwald führte, so haben sie mir vielleicht einen seltenen Gefallen getan. Verflucht sollen sie sein.' WHERE (`entry`='190');
UPDATE `itempages_localized` SET `text`='Aber vielleicht bin ich auch dazu verurteilt zuzusehen, was durch mich aus dem Dämmerwald werden wird. Vielleicht ist es mein Schicksal zuzusehen, wie die Worgen in dieses Land strömen, um es noch finsterer zu machen als es ohnehin schon ist.\nSollte dies so sein, so ist es doppelt schlimm. Denn die Worgs sind nicht die einzige Macht, die nach dem Dämmerwald greift - auch die Scheusale vom Gebirgspass der Totenwinde wollen das Land.\nDies ist das nächste Kapitel meiner Geschichte und ich bete, dass es das Letzte ist...' WHERE (`entry`='191');
UPDATE `itempages_localized` SET `text`='Nach meiner glücklichen Flucht aus Rolands Verdammnis versteckte ich mich im Heuschober eines Mannes mit Namen Sven. Ich verbrachte dort mehrere Tage und war dabei noch so voller Furcht, dass ich mich Sven und seiner Familie nicht ein einziges Mal zeigte. Aber aus dem, was ich von meinem Versteck aus sah, konnte ich schließen, dass es sich bei diesen einfachen Bauern um anständige Leute handelte. Hätte ich meinen Unterschlupf verlassen, wäre ich von diesen Leuten sicher mit offenen Armen aufgenommen worden. Aber Vertrauen zu fassen fällt mir schwer - und seit dem Schreck in der Mine umso mehr.\nAlso hielt ich mich weiter verborgen. Das rettete mir das Leben.' WHERE (`entry`='192');
UPDATE `itempages_localized` SET `text`='Einige Tage nach meiner Ankunft an der Scheune verließ Sven seinen Hof mit Ziel Dunkelhain. Er küsste seine Frau, lächelte seine Kinder an und versprach, bald mit Spielsachen und Süßigkeiten zurück zu sein. Der arme Mann. Es sollte das letzte Mal sein, dass er seine Familie lebend sah. \nWenigstens haben sie glücklich voneinander Abschied genommen. Und wenigstens starb seine Frau als Erste und musste den Tod ihrer Kinder nicht mit ansehen. Aber mir ist das kein Trost. Ich habe gesehen, was geschah, und es wird mich mein Leben lang verfolgen.' WHERE (`entry`='193');
UPDATE `itempages_localized` SET `text`='Meine Hand zittert, wenn ich mich an jene Nacht erinnere, als Sven fort und seine Familie allein den Schwarzen Reitern ausgeliefert war. Wieder mache ich mir Vorwürfe, denn ich war dort und hätte mich gegen jene Scheusale vom Gebirgspass der Totenwinde erheben können. Aber mein Bedauern ist nicht wahrhaftig. Es ist das Bedauern, wie es jeden plagt, der eine Tragödie überlebt hat. Ich weiß, hätte ich mein Versteck verlassen, so wäre ich auch getötet und meine Leiche bis zur Unkenntlichkeit verstümmelt und in alle Himmelsrichtungen verstreut worden.' WHERE (`entry`='194');
UPDATE `itempages_localized` SET `text`='Aber auch wenn ich weiß, dass ich diesen schändlichen Mord nicht hätte verhindern können, einen Vorwurf kann ich mir nicht ersparen: Ich habe die Schwarzen Reiter zu Svens Hof geführt. Meine Entdeckung der Sense hat nicht allein die Worgen auf den Dämmerwald losgelassen, sondern auch die Reiter vom Gebirgspass der Totenwinde her angelockt.\nDas weiß ich genau, denn unmittelbar vor dem Mord stellten sie Svens Frau eine Frage, während sie ihre Kinder umklammert hielt und ihnen im Angesicht des nahen Todes wenigstens ein wenig Trost spendete.' WHERE (`entry`='195');
UPDATE `itempages_localized` SET `text`='Die Sense von Elune, schrie einer der Reiter laut und schrill. Seine Stimme klang, als würde eine Axt an einem Stein geschliffen. Und das letzte Wort - Elune - krächzte er, als würde er daran ersticken.\nFurcht ergriff mich, als ich diese Stimme hörte, aber nicht allein ob ihres Klangs, nein … ich wusste, von welcher Sense der Reiter sprach. Es musste sich um das verfluchte Ding handeln, das ich ein paar Tage zuvor unter den Steinen in Rolands Verdammnis hervorgezogen hatte. Danach suchten die Schwarzen Reiter!\nUnd deswegen musste Svens Familie sterben.' WHERE (`entry`='196');
UPDATE `itempages_localized` SET `text`='Ich habe nie erfahren, wie Svens Frau eigentlich hieß. Ihr Mann und ihre Kinder nannten sie stets nur "Liebste", "meine Liebe" oder "Mami". Aber ich wünschte, ich wüsste ihren Namen. Denn ich bin der Einzige, der ihre Tat an jenem Tag noch bezeugen kann. Und obschon sie nur eine Bauersfrau war, habe ich nie einen Mann oder eine Frau so mutig und beherzt handeln sehen.\nNatürlich wusste sie nichts von der Sense, aber als sie begriff, dass die Reiter danach suchten, fasste sie spontan einen Entschluss.\nEinen kühnen und klugen noch dazu. Nur aufgegangen ist er nicht.' WHERE (`entry`='197');
UPDATE `itempages_localized` SET `text`='Die Sense?, fragte sie ganz ruhig. "Natürlich weiß ich, wo sie ist. Wer hier wüsste das nicht?" Sie blickte die Reiter vollkommen ruhig an und, hätte ich nicht genau gewusst, dass sie von der Sense nichts ahnen konnte, ich hätte geschworen, sie sagte die Wahrheit.\nIhr Wagnis schien sich auch auszuzahlen. Derselbe Reiter, der eben schon die Frage gestellt hatte, neigte den Kopf leicht zu ihr herunter und kreischte: "Wo?"\n"Ich werde Euch führen. Euch alle", erwiderte sie und ich konnte einen kleinen Hoffnungsschimmer in ihren Augen sehen.' WHERE (`entry`='198');
UPDATE `itempages_localized` SET `text`='Aber es ist ein weiter Weg und meine Kinder wären uns nur hinderlich. Wir müssen sie hier lassen.\nIhr Trick war einfach, aber sind es nicht die einfachen Tricks, die die größte Aussicht auf Erfolg haben? Wenn alles gut ging, würde sie die Reiter vom Hof weglocken. Sie selbst wäre verloren, aber ihre Kinder gerettet. Und es würde klappen, solange nur die Reiter ihren Lügen Glauben schenkten.\nIch war nie ein großer Anhänger des Lichts, aber in diesem Augenblick betete ich inbrünstig für Svens Frau, die sich so tapfer gegen die schrecklichen Reiter stellte.\n"Bitte!", betete ich. "Lass sie darauf hereinfallen!"' WHERE (`entry`='199');
UPDATE `itempages_localized` SET `text`='Die Reiter standen still, wie versteinert, und sie begegnete ihren Blicken mit aller Ruhe. Dann sah einer der Reiter auf, als höre er einen Ruf aus der Ferne. Er nahm einen kleinen Edelstein aus der Satteltasche. Damit machte er eine Geste in Richtung der Frau. Ein Licht kroch von dem Reiter aus auf die Frau zu und verformte sich zu einer unerbittlichen weißen Hand. Sie starrte scheinbar ungerührt in das Licht, aber ich sah die Unsicherheit hinter der Fassade des Selbstvertrauens. Als die Hand sie erreicht hatte, griff sie mit langen Fingern nach dem Kopf der Frau. \nUnd drückte zu.' WHERE (`entry`='200');
UPDATE `itempages_localized` SET `text`='Svens Frau stand steif wie ein Brett da und ihre Augen weiteten sich. Sie öffnete den Mund zu einem Schrei, brachte aber keinen Ton heraus. Nach einigen Augenblicken grausamer Folter ließ die Hand sie los und sie fiel auf die Knie. Der Reiter mit dem Schmuckstück saß aufrecht im Sattel und ließ dröhnend vernehmen:\n"Diese Frau lügt". Er sagte das mit einer Stimme, von der ich noch heute Alpträume bekomme. "Sie hat die Sense nie gesehen."' WHERE (`entry`='201');
UPDATE `itempages_localized` SET `text`='Danach ließ der Reiter die Schultern sinken, als sei ein Geist aus ihm entwichen. Und dann sagte er mit der alten, schrillen Stimme von zuvor die todbringenden Worte:\n"Der Lord hat gesprochen. Tötet sie."' WHERE (`entry`='202');
UPDATE `itempages_localized` SET `text`='Ich kann nicht beschreiben, was danach geschah. Ich habe es deutlich vor Augen, aber meiner elenden Seele ist es noch immer nicht möglich niederzuschreiben, was sich in den folgenden, grausigen Minuten ereignete.\nIch kann nur schreiben, dass Svens Familie getötet wurde. Kurz darauf kehrte Sven an den Schauplatz des Schreckens zurück. Er war derart von Trauer übermannt, dass ich Angst hatte, mich ihm zu zeigen. Und ich fürchtete mich sogar davor, dass er mich finden würde, und floh aus meinem Versteck in der Scheune. Ich weiß nicht, wo Sven jetzt ist, aber ich bete, dass er eines Tages Frieden findet.' WHERE (`entry`='203');
###
# 204
###
UPDATE `itempages_localized` SET `text`='Nachdem ich die Sense gefunden hatte, stürzten sie sich von allen Seiten auf uns, kamen aus versteckten Löchern zu unseren Füßen oder ließen sich von oben lautlos auf uns herabfallen. Die Hälfte der Männer fiel schon in diesen ersten panikerfüllten Minuten. Der Rest - darunter auch ich - versuchte zu fliehen. Ich rannte und hörte von allen Seiten, wie meine Brüder von Klauen und Zähnen zerrissen wurden. Ich hörte unzählige Schreie, die abrupt verstummten oder in einem Gurgeln erstarben.' WHERE (`entry`='205');
UPDATE `itempages_localized` SET `text`='Ich kann nur raten, warum ich diese Nacht überlebt habe. Ich war immer schon vorsichtig, bin Schlägen ausgewichen und habe einen Bogen um Fallgruben gemacht. Daher kommt auch mein Spitzname. Vielleicht war es also dieser Hang zur Vorsicht, der mich gerettet hat...\nOder es war tatsächlich diese Sense, die ich aus dem Geröll gezogen habe. An der Sense selbst kann es indes nicht liegen, denn die habe ich während meiner panischen Flucht verloren. Aber wenn ich es war, der die Worgen in den Dämmerwald führte, so haben sie mir vielleicht einen seltenen Gefallen getan. Verflucht sollen sie sein.' WHERE (`entry`='206');
UPDATE `itempages_localized` SET `text`='Nach meiner glücklichen Flucht aus Rolands Verdammnis versteckte ich mich im Heuschober eines Mannes mit Namen Sven. Ich verbrachte dort mehrere Tage und war dabei noch so voller Furcht, dass ich mich Sven und seiner Familie nicht ein einziges Mal zeigte. Aber aus dem, was ich von meinem Versteck aus sah, konnte ich schließen, dass es sich bei diesen einfachen Bauern um anständige Leute handelte. Hätte ich meinen Unterschlupf verlassen, wäre ich von diesen Leuten sicher mit offenen Armen aufgenommen worden. Aber Vertrauen zu fassen, fällt mir schwer - und seit dem Schreck in der Mine umso mehr.\nAlso hielt ich mich weiter verborgen.' WHERE (`entry`='207');
UPDATE `itempages_localized` SET `text`='<Die restlichen Seiten des Buchs sind leer.>' WHERE (`entry`='208');
UPDATE `itempages_localized` SET `text`='BERICHT: Kobolde\nDie Aktivität der Kobolde hat in der Gegend des Nordhaintals zugenommen. Sie lagern in beunruhigend großer Zahl nördlich der Abtei und in der Echokammmine. Ich habe Paladine, Krieger und Ortsansässige angewiesen, mir bei der Vertreibung dieses Gezüchts zur Hand zu gehen.\nWir sind dabei recht erfolgreich. Ich behalte mir gegebenenfalls weitere Schritte vor.' WHERE (`entry`='209');
UPDATE `itempages_localized` SET `text`='BERICHT: Diebe\nBeunruhigender als die Kobolde ist indes das Auftauchen einer menschlichen Diebesbande im Tal. Diese Diebe tragen rote Kopftücher und schimpfen sich eine "Bruderschaft". Diese Stufe der Organisation könnte sowohl hier als auch im Wald von Elwynn generell für Schwierigkeiten sorgen.\nWir haben einen ihrer Anführer als Garrick Schleichfuß ausmachen können und hoffen, ihn bald vor Gericht zu bringen.' WHERE (`entry`='210');
UPDATE `itempages_localized` SET `text`='BERICHT: Wölfe\nIn letzter Zeit wird in Nordhain verstärkt von Angriffen durch Tiere berichtet. Eagan glaubt, dass die Wölfe im Tal aus dem Dämmerwald hierher verscheucht worden sind.\nZwar machen die Wölfe nur selten Jagd auf Menschen, aber allein durch ihr vermehrtes Auftreten ist es bereits zu Zwischenfällen mit Bauern gekommen. Zudem wurde bereits viel Vieh gerissen.' WHERE (`entry`='211');
UPDATE `itempages_localized` SET `text`='EMPFEHLUNG:\nDem Inhaber dieser Dokumente wird der Status eines Stellvertreters in der Armee von Sturmwind verliehen, weil er Nordhain mit Eifer und Entschlossenheit gedient hat. Ich bin der Überzeugung, dass Euch diese Person im Wald von Elwynn noch von Nutzen sein wird.\nGezeichnet:\n- Marschall Douglas McBride, \nArmee von Sturmwind, Nordhain' WHERE (`entry`='212');
UPDATE `itempages_localized` SET `text`='Westfalleintopf  \n3 Teile sehniges Geierfleisch \n3 Geiferzahnschnauzen \n3 Murlocaugen \n3 Okraschoten\nVermischen und aufkochen lassen. Mindestens zwei Stunden köcheln lassen, dann servieren.' WHERE (`entry`='213');
###
# 214 - 217
###
UPDATE `itempages_localized` SET `text`='Ich fürchte, die Fehde zwischen den Steinfelds und den Maclures wird die zarte Romanze zwischen Tommy Joe und Maybell zerstören. Und gerade in Zeiten wie diesen, da es ständig neue schlechte Nachrichten gibt und Gerüchte über einen bevorstehenden Krieg an der Tagesordnung sind, müssen die Liebe und die Jugend geschützt werden.\nDeswegen erbitte ich also Folgendes von Euch: Ich bitte Euch, mit Euren Fertigkeiten einen Trank oder ein Elixier herzustellen, das diesen beiden Liebenden auf der Suche nacheinander hilft.\nHabt Dank, William. Und bitte, solltet Ihr neben Eurer Arbeit etwas Zeit erübrigen können, so kommt doch vorbei. Dann können wir ein wenig über vergangene Zeiten plaudern.\n- Mildred' WHERE (`entry`='218');
UPDATE `itempages_localized` SET `text`='Aus der Feder von Baros Alexston,\nBüro des Stadtarchitekten von\nSturmwind\nEure Majestät!\nEs gibt der Gründe viele, warum ich glaube, hinsichtlich der "Bruderschaft der Defias" und ihrer jüngsten Aktivitäten im ganzen Königreich sollte ein Bericht abgefasst und vorgelegt werden.\nLasst mich der Klarheit halber mit einem kleinen Exkurs in die Vergangenheit beginnen. Wie Euch vielleicht bekannt ist, begann ich meine Arbeit in Diensten der Stadt Sturmwind als Mitglied der Steinmetzgilde. Nach Jahren der Arbeit beendeten wir schließlich den Wiederaufbau von' WHERE (`entry`='219');
UPDATE `itempages_localized` SET `text`='Sturmwind, aber niemand wollte die Steinmetzgilde bezahlen oder für die Bezahlung bürgen.\nZu dieser Zeit war Edwin van Cleef der gewählte Gildenmeister der Steinmetze. Als solcher ergriff er das Wort und forderte das Entgelt für unsere Arbeit ein. Als Reaktion darauf ordnete das Oberhaus von Sturmwind die Auflösung der Steinmetzgilde an, was van Cleef verständlicherweise sehr erzürnte. Unter seiner Führung kam es zum Aufstand und van Cleef führte die Steinmetze aus der Stadt.\nDoch ehe ich nun fortfahre, muss ich auf einige andere Ereignisse zu sprechen kommen, die' WHERE (`entry`='220');
UPDATE `itempages_localized` SET `text`='sich zu jener Zeit ereignet haben.\nZunächst einmal wurde mir der Posten des Stadtarchitekten angetragen, wenn ich mich van Cleef nicht anschlösse. Wegen gewisser ideologischer Differenzen entschied ich mich für den Verbleib in Sturmwind.\nWährend des Aufstands wurde Bazil Thredd, van Cleefs Helfer und Vertrauter, gefangen genommen und eingesperrt. Thredd sollte verhört und vor Gericht gestellt werden, wurde dann aber fast im Verlies vergessen.\nEr kehrte zu van Cleef zurück, nachdem er den Rest der' WHERE (`entry`='221');
UPDATE `itempages_localized` SET `text`='Steinmetze aus Sturmwind geführt hatte. Danach machte er sich die relative Schutzlosigkeit von Westfall zunutze, vertrieb dank zahlenmäßiger Überlegenheit viele der Bauern und übernahm eine Hand voll der Goldminen.\nMit diesen Rohstoffen in der Hinterhand fasste van Cleef schließlich einen Racheplan gegen die Regierung von Sturmwind.\nDiese Erkenntnis erlangten wir unter Mithilfe des Inhabers des vorliegenden Dokuments, der uns bei der Aufdeckung der weit reichenden Verschwörung geholfen hat.' WHERE (`entry`='222');
UPDATE `itempages_localized` SET `text`='Es steht Euch frei, diese Informationen weiterzugeben.\r\n\r\nRegionalkommandantur Tirisfal\r\nHauptmann Melrose\r\nHauptmann Vachon\r\nHauptmann Perrine\r\n\r\nAnweisungen auf Befehl des Hochlords\r\n\r\nHauptmann Perrine, baut Eure Position am Euch zugewiesenen Südwestturm weiter aus. Weiterer Nachschub wird zu einem späteren Zeitpunkt entsandt. Bis dahin versorgt Euch auf den umliegenden Gehöften mit allem Nötigen. Zudem benötigen wir weitere Informationen über' WHERE (`entry`='223');
UPDATE `itempages_localized` SET `text`='die Organisation der Untoten in Brill.\r\n\r\nHauptmann Vachon, es scheint verstärkt Bewegung unter den Untoten am nördlichen Turm zu geben. Damit muss schnell und gründlich Schluss sein.\r\n\r\nHauptmann Melrose, der Grad der Organisation unter den Untoten nahe der Pestländer bereitet einige Sorgen. In den kommenden Wochen bekommt Ihr neue Männer.\r\n\r\nRuhm unter dem Lichte!' WHERE (`entry`='224');
###
# 225 - 234
###
UPDATE `itempages_localized` SET `text`='Seid gegrüßt, Ello Schwarzhaupt, Bürgermeister von Dunkelhain. Ich habe leider Nachrichten für Euch. Schlechte Nachrichten.\nIch bin ein Schöpfer. Ich habe den bedauernswerten Überbringer dieser Notiz überlistet und zu einem Teil meiner jüngsten Schöpfung gemacht - einem Scheusal aus Fleisch und Knochen und Metall! Während Ihr dies lest, wartet das Wesen wahrscheinlich zähneknirschend draußen vor meinem bescheidenen Heim auf meinen Befehl, um loszuziehen und zu töten.\nAber das erfahrt Ihr noch früh genug.\n- Der Einbalsamierer' WHERE (`entry`='235');
UPDATE `itempages_localized` SET `text`='Nach eingehenden Studien diverser Fossilien habe ich nun herausgefunden, dass der Lordameresee vor langer Zeit von einer Seuche befallen gewesen sein muss. Was war die Ursache? Wir werden es vielleicht nie erfahren. Aber angesichts der vielen sterblichen Überreste im See muss das Wasser sehr giftig gewesen sein.' WHERE (`entry`='236');
UPDATE `itempages_localized` SET `text`='Um mehr über die Vergangenheit zu erfahren, habe ich begonnen, die Kreaturen der Gegenwart zu untersuchen - immer in der Hoffnung, so das fehlende Glied zur Lösung des Geheimnisses zu entdecken. Bei den Seeschleichern und den Seekrabblern handelt es sich um uralte Wesen, die auf den Inseln im Lordameresee leben. Es wächst ein Moos auf ihnen, das den auf den Fossilien gefundenen Spuren gleicht. Ohne weitere Forschungen ist es jedoch zu früh für Spekulationen, was diese Verbindung zu bedeuten hat.' WHERE (`entry`='237');
UPDATE `itempages_localized` SET `text`='Auf meiner Suche nach Moosproben stieß ich auf den Schauplatz eines blutigen Kampfes. Der Vilefinstamm der Murlocs war von einer marodierenden Bande Gnolle belagert worden. Überall lagen tote Gnolle und Murlocs herum. Als ich an einer solchen zerschundenen Murlocleiche vorbeikam, fiel mir ein merkwürdig verhärteter Tumor auf, der aus einer der Wunden wuchs. Ich begann rasch mit der Untersuchung des Tumors und stellte fest, dass er ähnliche Eigenschaften aufwies wie das Moos, das ich sammelte. Leider fand ich keine weiteren Tumore.' WHERE (`entry`='238');
###
# 239 - 242
###
UPDATE `itempages_localized` SET `text`='Auf meiner Suche nach Moosproben stieß ich auf den Schauplatz eines blutigen Kampfes. Der Vilefinstamm der Murlocs war von einer marodierenden Bande Gnolle belagert worden. Überall lagen tote Gnolle und Murlocs herum. Als ich an einer solchen zerschundenen Murlocleiche vorbeikam, fiel mir ein merkwürdig verhärteter Tumor auf, der aus einer der Wunden wuchs. Ich begann rasch mit der Untersuchung des Tumors und stellte fest, dass er ähnliche Eigenschaften aufwies wie das Moos, das ich sammelte. Leider fand ich keine weiteren Tumore.' WHERE (`entry`='243');
UPDATE `itempages_localized` SET `text`='Lord Bethor Iceshard,\n\nwie von Euch verlangt, schicke ich Euch den angeforderten Agenten. Es ist der Überbringer dieser Notiz.\n\nDiese Person hat sich als verlässlich und gehorsam erwiesen und ist erprobt im Kampf gegen die Geißel. Daher ist sie meine Wahl für Euren Auftrag.\n\nMöge sie ewig herrschen!\n- Magistrat Sevren' WHERE (`entry`='244');
UPDATE `itempages_localized` SET `text`='Vermisst wird: Korporal Keeshan\nZuletzt gesehen wurde er während der blutigen Schlacht vor Burg Steinwacht. Wie berichtet, wurde Korporal Keeshan von Schwarzfelsorcs verschleppt.\nWir gehen davon aus, dass Korporal Keeshan als Kriegsgefangener im Schwarzfelslager nördlich von Seenhain festgehalten wird, das sich westlich des Passes befindet, der zur brennenden Steppe führt.\nAuf Anweisung von Magistrat Solomon erhält jeder, der zur Rettung von Korporal Keeshan beiträgt, eine Belohnung von Marschall Marris.' WHERE (`entry`='245');
UPDATE `itempages_localized` SET `text`='In den darauf folgenden Wochen blieb ich aus Angst vor den Reitern nie allzu lange an einem Ort. Augenblicklich befinde ich mich in der verlassenen Stadt Rabenflucht und wie immer verstecke ich mich. Ich weiß nicht, welche Kräfte sie gegen Svens Frau eingesetzt haben, aber ich weiß, dass ich gegen diese Kräfte nichts ausrichten kann und dass sie den Dämmerwald auch weiterhin nach der Sense absuchen. Ich habe die Sense nicht mehr und ich danke dem Licht dafür, denn hätte ich sie noch, hätten sie mich längst gefunden. Aber auch so weiß ich tief in meinem Herzen, dass sie mich finden werden.\nIch bin so müde.' WHERE (`entry`='246');
###
# 247
###
UPDATE `itempages_localized` SET `text`='Seid gegrüßt, Meister.\nVergebt mir, dass ich Euch diese Nachricht auf so ungewöhnliche Weise schicke. Meine Schattenmagier in Alterac sind so mit ihren Aufgaben beschäftigt, dass ich mich gezwungen sehe, für die Übersendung dieses Berichts einen fremden Boten einzusetzen, den wir von unseren neuen "Verbündeten" rekrutiert haben. Ich habe diesen Brief jedoch mit einem hochesoterischen Geheimcode verschlüsselt. Von diesen Dummköpfen kann ihn keiner lesen.' WHERE (`entry`='248');
UPDATE `itempages_localized` SET `text`='Valik,\nIhr bewacht den Sklaven bis zu unserer Rückkehr. Es ist nicht sicher, das Ding länger hier zu behalten. Wir bringen es nach Norden, wo es nicht so leicht zu sehen ist. \n\nDas sind zwar Tiere, aber brutale Tiere - in großer Zahl gäben sie uns Anlass zur Sorge. Man verbirgt besser alles, was sie provozieren könnte. Wir wollen ihnen keinen Grund geben, uns in Scharen anzugreifen.' WHERE (`entry`='249');
UPDATE `itempages_localized` SET `text`='Eine Gruppe vom Hauptquartier der Defias wird mit dem Sammler in Kontakt treten und dieser wird das gesammelte Gold übergeben.\nDieser Vorgang muss reibungslos und äußerst diskret ablaufen. Der Sammler ist für die Übergabe des Golds verantwortlich, doch letztlich liegt es auch an jedem Mitglied der Bruderschaft selbst, dafür zu sorgen, dass es seine Rolle sorgsam und diszipliniert ausfüllt.' WHERE (`entry`='250');
UPDATE `itempages_localized` SET `text`='Bedenkt, meine Brüder, wir waren einst stolze Handwerker. Wir werden unsere jetzigen Pflichten ebenso sorgsam erfüllen wie wir in der Vergangenheit unserem Handwerk nachgegangen sind.\n- EVC' WHERE (`entry`='251');
UPDATE `itempages_localized` SET `text`='Ich fürchte, mein Vater hat einen großen Fehler begangen, als er beschloss, zu bleiben und unsere Heimat zu verteidigen. Die Geißel wütet durch Tirisfal, und obwohl wir gut aufgestellt und versorgt sind, fehlt es uns nach dem Verrat meines Bruders Devlin doch an Kräften, um dem ständigen Ansturm standzuhalten.$B Wenn du diesen Brief erhältst, Yvette, dann weißt du, Devlin ist ein Verräter. ' WHERE (`entry`='252');
UPDATE `itempages_localized` SET `text`='Devlin verzweifelte und suchte nach einem Ausweg aus unserer schlimmen Lage und in dieser seiner Verzweiflung ging er einen Pakt mit der Geißel ein. Ich weiß nicht, was ihm als Belohnung für seinen Verrat versprochen worden ist, aber ich bin mir ohnehin sicher, dass nicht ein Wort davon wahr war.$B Solltet Ihr auf meinen Bruder treffen, dann flieht vor ihm. Er ist eine Satanskreatur. Erst vor ein paar Nächten tötete er eigenhändig zwei unserer Wachposten und führte eine kleine Gruppe der Geißel durch die Bresche an den Ort, wo viele andere von uns ihr Nachtlager aufgeschlagen hatten. ' WHERE (`entry`='253');
UPDATE `itempages_localized` SET `text`='Der Hinterhalt war brutal. Ein Drittel von uns fiel, ehe wir sie besiegt hatten. Jetzt haben wir kaum genug Männer zur Bewachung unserer Grenzen. Es ist nur eine Frage der Zeit, bis unsere erschöpfte Wache fällt und die Geißel angreift.$B Und obwohl ich in dieser verfluchten Nacht das Gesicht von Devlin unter den verzweifelten Männern und den Fratzen der Geißel sah, ist er mir doch entkommen. Vielleicht ist er tot, vielleicht noch immer ein Spion der Geißel.$B In jedem Fall ist er ein Ungeheuer. ' WHERE (`entry`='254');
#/* Nicht Komplett!*/#UPDATE `itempages_localized` SET `text`='Devlin war immer schon schwer zu durchschauen. Schon als Kind gab er uns Rätsel auf. ' WHERE (`entry`='255');
UPDATE `itempages_localized` SET `text`='Aber er hatte Recht. Wir sind dem Untergang geweiht. Die Familie Agamand wird es bald nicht mehr geben. Zwar lebe ich noch, doch weiß ich, dass ich bald schon tot sein werde.$B Ich habe keine Angst und ich bereue nicht, bei meiner Familie geblieben zu sein. Aber meine letzten Gedanken werden dir gehören.$B Lebe weiter, meine geliebte Yvette, und ich werde Frieden finden.$B - Thurman ' WHERE (`entry`='256');
UPDATE `itempages_localized` SET `text`='Unsere Pläne schreiten rasch voran. Wie erwähnt sind wir eine Allianz mit einer Gruppe von Menschen eingegangen, die sich selbst das "Syndikat" nennen. Die Anführer dieser Gruppe stellten einst den Adel von Alterac und sind ganz erpicht darauf, ihre alte Vormachtstellung zurückzubekommen.\nDeshalb sind sie einen Handel mit uns eingegangen. Einen, den sie noch bereuen werden.' WHERE (`entry`='257');
UPDATE `itempages_localized` SET `text`='Heute hat das Syndikat in diesem Landstrich nur noch wenig Macht, doch kennt es die Gegend gut und verfügt über genügend Männer für die anstehenden Aufgaben. Ich bin sicher, dass das Syndikat bei entsprechender Führung Alterac bald wieder beherrschen wird.\nZudem haben wir das Syndikat gedrängt, auf die Stadt Stromgarde im nahe gelegenen Arathihochland vorzurücken. Die Stadt ist das Schlachtfeld zwischen unseren Truppen, Ogern und den Menschen von Stromgarde, aber mit einer sorgfältig ausgearbeiteten Taktik dürfte diese einst große Stadt bald uns gehören.' WHERE (`entry`='258');
UPDATE `itempages_localized` SET `text`='Sobald das erledigt ist, kann die nächste Phase unseres Plans beginnen. Mit einem eigenen Land und einem großen Stützpunkt in diesem Gebiet können wir dann die Ankunft des Dritten Wirtskörpers vorbereiten.\nBald schon werden die Lords der Legion uns entlohnen, indem sie wieder Feuer vom Himmel regnen lassen!' WHERE (`entry`='259');
###
# 260 - 265
###
UPDATE `itempages_localized` SET `text`='Kegan Darkmar, der Anführer jener kleinen Gruppe von Untoten, die auf der Suche nach Schutz vor ihren "Brüdern" zu uns kam, ist so gar nicht wie die anderen von seinem Schlag. Seine Haut mag verfaulen und Blut mag schon lange nicht mehr durch seine Adern fließen, aber seine Handlungen sind edel und er scheint sich mehr um das Wohl seiner Begleiter zu sorgen, als um sein eigenes Wohlergehen.\n\nIn der Tat zeichnet er sich durch eine Menschlichkeit aus, die ich, wie ich gestehe, gelegentlich an den Menschen um mich herum vermisse.' WHERE (`entry`='266');
UPDATE `itempages_localized` SET `text`='Aber warum erwähne ich das? Nun, ich möchte dem, was ich nun niederschreiben werde, auf diese Weise Glaubwürdigkeit verleihen, denn dies sind die Worte von Kegan. Ich hoffe, dass meine Kollegen, wenn sie dies Tagebuch lesen, verstehen werden, warum ich ihm Glauben geschenkt habe:\n\n"Überbleibsel der Alten Götter liegen noch immer in den Tiefen der Welt verborgen. Nun wollen sich neue Kräfte diese uralte Macht zu Nutze machen, und wenn sie Erfolg haben, halten sie eine furchtbare Waffe gegen ihre Feinde in Händen."' WHERE (`entry`='267');
UPDATE `itempages_localized` SET `text`='Mit diesen Worten hat mir Kegan seinen Blutsteinanhänger übergeben, und in seinen Augen lag dabei ein Ausdruck von Angst und vielleicht sogar so etwas wie Ehrfurcht. Und als er die Steine in meine Hand legte, blieben seine Augen darauf ruhen, als gebe er den Anhänger nur widerwillig ab. Ekel überkam mich, aber ich weiß bis heute nicht, ob das an der Berührung durch Kegans totes Fleisch lag oder am Anhänger selbst.\n\nDenn ich fühlte darin eine Kraft. Eine tiefe, verborgene, hungrige Kraft. Eine Kraft, die sich nach Freiheit sehnte.' WHERE (`entry`='268');
UPDATE `itempages_localized` SET `text`='Auch wenn meine Kollegen in Dalaran arge Bedenken hatten, die von Kegan und seinen Begleitern mitgebrachten Blutsteine zu untersuchen, so fühlte ich mich durch Kegans Ehrlichkeit doch gezwungen, seinen Blutsteinanhänger zu untersuchen, statt ihn und die vier anderen einzusperren und die Steine zu lassen, wo sie waren.' WHERE (`entry`='269');
UPDATE `itempages_localized` SET `text`='Ich hoffte, meinen Kollegen davon überzeugen zu können, dass diese Steine in der Tat magische Kräfte besäßen und dass, auch wenn wir Hexer von Dalaran die Macht der Blutsteine nicht nutzen wollten, wir wenigstens ihre Eigenschaften studieren sollten, da der Feind sie eines Tages gegen uns verwenden könnte.\n\nUnd so begann ich denn.' WHERE (`entry`='270');
UPDATE `itempages_localized` SET `text`='Ich begann meine Prüfungen zunächst in der Annahme, dass es sich bei Blutstein tatsächlich um Gestein wie Quarz oder Obsidian handelt. Also machte ich verschiedene Prüfungen zur Gesteinsbestimmung: welche Mineralien der Blutstein enthielt, wie seine Farbe und sein Härtegrad zustande kamen sowie einige andere für Gestein und Erz übliche Eigenschaften. Aber der Blutsteinanhänger reagierte frustrierenderweise auf keine der Prozeduren wie normales Erz.' WHERE (`entry`='271');
UPDATE `itempages_localized` SET `text`='Genau genommen reagierte er sogar genau andersherum als erwartet. Fast schien es, als sabotiere der Anhänger meine Experimente absichtlich,\n\nals besäße er einen Verstand und sei lebendig.\n\nVerärgert, aber nicht entmutigt ging ich nun also davon aus, es handele sich um etwas Lebendiges. \n\nVergeblich.' WHERE (`entry`='272');
UPDATE `itempages_localized` SET `text`='Meine Experimente konnten die Herkunft des Blutsteins nicht enthüllen. Das einzige Rätsel, das ich lösen konnte, war, dass der Blutstein weder lebendig noch tot war!\n\nAusgerechnet dann gelang mir aber plötzlich der Durchbruch. Für mein jüngstes Experiment benötigte ich ein Becherglas, dessen Rand an einer Stelle etwas abgesplittert war. Das eigentliche Experiment erbrachte wieder kein Ergebnis, aber als ich danach den Arbeitstisch reinigte, schnitt ich mir mit dem Becherglas in den Finger.' WHERE (`entry`='273');
UPDATE `itempages_localized` SET `text`='Die Wunde war nicht tief, blutete aber stark. Bevor ich meinen Finger verarzten konnte, war mein Schreibtisch schon voller Blut.\n\nIch wollte es wegwischen, aber da fiel mir etwas höchst Merkwürdiges auf...' WHERE (`entry`='274');
UPDATE `itempages_localized` SET `text`='Das Blut, das neben dem Blutsteinanhänger auf den Tisch getropft war, bewegte sich langsam auf das Schmuckstück zu, als würde es davon angezogen. Die Blutstropfen, die mit dem Anhänger in Berührung kamen, schienen zu verschwinden, und das Rot des Steins wurde umso intensiver, je mehr von meinem Blut er in sich aufnahm.' WHERE (`entry`='275');
UPDATE `itempages_localized` SET `text`='Als ich das sah, wurde mein Kopf ganz leicht, was vielleicht weniger an meiner Verletzung lag (denn so viel Blut hatte ich ja nicht verloren) als an der Tatsache, dass ich nach all dem Frust endlich eine der Eigenschaften des Blutsteins gefunden hatte. Ich griff nach einem Hocker hinter mir, setzte mich und dachte nach. Gedanken und Fragen rasten so schnell durch meinen Kopf, dass mir ganz schwindelig davon wurde.\n\nTrinkt der Blutstein Blut? Verlangt er nach Blut? Zieht er Blut an?' WHERE (`entry`='276');
UPDATE `itempages_localized` SET `text`='Oder besteht Blutstein gar aus Blut? Und wenn ja, wessen Blut? Mein Blut? Menschenblut? Blut von Tieren? \n\nVielleicht ist der Blutstein aber auch aus dem Blut eines unbekannten Dings - eines Dings, das Kegan sowohl fürchtete als auch verehrte, als er mir seinen Anhänger gab.\n\nDiese Frage gilt es zu beantworten. Sie ist der Schlüssel.' WHERE (`entry`='277');
UPDATE `itempages_localized` SET `text`='Mein Ehrgeiz wurde angestachelt und ich stürzte mich wieder in meine Experimente. Dieses Mal hielt ich mich nicht lange mit Vermutungen auf, sondern machte jeden mir nur möglichen Test. Freilich war der Aufwand an Zeit und Mühe beträchtlich, aber es erhöhte auch die Wahrscheinlichkeit neuer Entdeckungen. \n\nUnd obwohl mein Labor sehr klein ist und ich niemanden habe, der mir hilft, ist es mir doch gelungen, eine weitere faszinierende Eigenschaft der Blutsteine zu finden...' WHERE (`entry`='278');
UPDATE `itempages_localized` SET `text`='Neben Blut sind auch elementare Kräfte in dem Stein konzentriert. Feuer, Wasser, Donnern und Fels sind mit dem Blut vermischt (aber die Frage bleibt: wessen Blut eigentlich?). Und obwohl diese Mixtur äußerlich eher träge erscheint, scheinen doch all diese Kräfte innerlich miteinander zu ringen. Fragen über Fragen taten sich auf angesichts dieses ebenso faszinierenden wie beunruhigenden Materials.' WHERE (`entry`='279');
UPDATE `itempages_localized` SET `text`='Aber um diese Frage beantworten zu können, müssen weitere Studien und Experimente an dem Anhänger durchgeführt werden, und ich befürchte, dass es im Internierungslager von Lordamere weder genug Arbeitskräfte noch die erforderliche Ausrüstung dazu gibt. Also übergab ich den Blutsteinanhänger einem Kurier, der ihn nach Dalaran bringen sollte. Ich gab ihm auch genaue Anweisungen für die Experimente mit, auf dass meinen Kollegen die frustrierenden Erfahrungen erspart blieben, die ich gemacht hatte.' WHERE (`entry`='280');
UPDATE `itempages_localized` SET `text`='Während ich auf die Ergebnisse dieser Experimente wartete, sprach ich wieder öfter mit Kegan. Ich drängte ihn immer wieder, mir zu sagen, was er über die Blutsteine wusste. Aber nie verriet er mehr als das, was er mir schon gesagt hatte, als er mir den Anhänger gab, wie er überhaupt nicht oft über "die Verlassenen" sprach, wie er seinen Klan der Untoten nennt.' WHERE (`entry`='281');
UPDATE `itempages_localized` SET `text`='Aber Kegan war ganz erpicht darauf, über andere Themen zu reden, insbesondere seine Zeit in Lordaeron vor dessen Fall. \n\nEr liebt dieses untergegangene Königreich noch immer, obwohl es doch nicht mehr existiert.\n\nMeine wachsende Sympathie für Kegan verlieh mir Geduld, während ich auf die Untersuchungsergebnisse wartete.' WHERE (`entry`='282');
UPDATE `itempages_localized` SET `text`='Nach Wochen ohne Nachricht verlor ich die Geduld, forschte nach und erfuhr, dass der Blutstein nie in Dalaran bei meinen Kollegen angekommen war. Mein Kurier war verschollen und der Blutsteinanhänger mit ihm!\n\nDas sind schlechte Neuigkeiten, denn obschon Kegan und seine Begleiter noch einige andere Blutsteinexemplare haben mit denen wir experimentieren können, befürchte ich, der Anhänger könnte in die falschen Hände geraten.' WHERE (`entry`='283');
UPDATE `itempages_localized` SET `text`='Ich habe einen weiteren Boten nach Dalaran geschickt und gehört, dass sie dort immer noch nach dem Anhänger suchen, und zwar inzwischen auch in den Ruinen außerhalb unserer schützenden Sphäre.\n\nIch kann nur hoffen, dass es nicht zu spät ist.' WHERE (`entry`='284');
UPDATE `itempages_localized` SET `text`='Die Geschichte von Gri''lek dem Wanderer\n[Der obere Teil der Schrifttafel war ganz abgenutzt. Aber der Rest war durchaus lesbar...]\nGri''lek stapfte durch den Urwald. Seine Augen brannten und sein Herz hämmerte, denn er war sehr zornig. \nVoller Wut hob er seinen Arm und brüllte gen Himmel. Es war sein linker Arm, den er hob, stark und sicher geworden von der Jagd ohne seinen Zwilling. \nDenn Gri''leks rechter Arm war auf immer fort.' WHERE (`entry`='285');
UPDATE `itempages_localized` SET `text`='LEGENDEN DER TROLLE, BAND III\nDer Stein der Gezeiten\nNiedergeschrieben von \nErzmagier Ansirem Runenweberr\nDalaran\nEINLEITUNG\nDas alte Reich der Gurubashi war die Quelle vieler faszinierender Legenden, die zweifellos in engem Zusammenhang mit der unmittelbaren Umgebung stehen, zumal Untersuchungen religiöser und sozialer Praktiken eine enge Verbundenheit mit der Natur ergeben haben.\nIch habe mich eingehend befasst mit' WHERE (`entry`='286');
UPDATE `itempages_localized` SET `text`='Kurdran Wildhammer\nBerühmter Drachenkämpfer. Greifenmeister des Nistgipfels. Kommandant der Greifenreiterdivision, die als Teil der Expedition der Allianz in Draenor, der Heimat der Orcs, einmarschiert ist. Vermutlich gefallen.\nWir werden Hori''zees Rufe im Wind hören. Wir werden deinen Hammer über den Berggipfeln donnern hören. Reite geradewegs in das Danach, Bruder. Die Hallen unserer Vorfahren warten auf dich.\n- Oberster Than Falstad Wildhammer' WHERE (`entry`='287');
UPDATE `itempages_localized` SET `text`='Waldläuferhauptmann Alleria Windläufer\nBerühmte Trolljägerin aus Quel''Thalas. Hauptspäherin und Geheimagentin für die Expedition der Allianz, die in Draenor, der Heimat der Orcs, einmarschiert ist. Vermutlich gefallen.\nDu bist geflogen wie ein Pfeil im Wind, Schwester. Du warst die Beste unseres Ordens. Du warst die Teuerste unserer Sippe.\n- Sylvanas Windläufer - Waldläufergeneral von Quel''Thalas' WHERE (`entry`='288');
UPDATE `itempages_localized` SET `text`='Erzmagier Khadgar von den Kirin Tor\nEhemaliger Lehrling von Medivh. Oberkommandeur der Expedition der Allianz, die in Draenor, der Heimat der Orcs, einmarschiert ist. Vermutlich gefallen. \nNoch nie hat sich jemand so selbstlos ins finstere Herz von Magie und Krieg begeben. Wir wünschen euch alles Gute, kühner Wanderer. Wo immer Ihr auch seid.\n- Antonidas - Erzmagier von Dalaran' WHERE (`entry`='289');
UPDATE `itempages_localized` SET `text`='General Turalyon Ehemaliger Lieutenant von Lord Anduin Lothar. Ritter der Silberhand, Hochgeneral in der Expedition der Allianz, die in Draenor, der Heimat der Orcs, einmarschiert ist. Vermutlich gefallen. Esarus thar no''Darador'' - durch Blut und Ehre dienen wir. Du warst die rechte Hand von Gerechtigkeit und Tugend, alter Freund. Dein Name wird in unseren Hallen auf ewig verehrt werden. - Lord Uther Lightbringer - Ritter der Silbernen Hand ' WHERE (`entry`='290');
UPDATE `itempages_localized` SET `text`='Danath Trollbann\nMilizkommandant von Stromgarde. Taktischer Berater von General Turalyon, Kommandant der Allianzexpedition, die in Draenor, der Heimat der Orcs, einmarschierte. Vermutlich gefallen.\nWir ehren dein Andenken, Neffe, und dein Opfer. Seit Gründung unseres Imperiums ist der Weg zur Tapferkeit mit dem Blut von Helden getränkt.\n- Thoras Trollbann, Lord von Stromgarde' WHERE (`entry`='291');
UPDATE `itempages_localized` SET `text`='Hier ruht Uther Lichtbringer\nErster Paladin - Gründer des Ordens der Silberhand\nUther lebte und starb für das Königreich Lordaeron. Obwohl er von seinem liebsten Schüler verraten wurde, glauben wir, dass sein Geist weiter lebt. Er wacht weiter über uns, auch wenn die Schatten unser verdorbenes Land langsam einschließen. Sein Licht ist das Licht der Menschlichkeit - solange wir seinem Beispiel folgen, wird es ewig leuchten. \n									- Anonymus' WHERE (`entry`='292');
UPDATE `itempages_localized` SET `text`='Aspekten ihrer Natternverehrung in den früheren Bänden dieser Studie füge ich an dieser Stelle noch eine Untersuchung der interessanten und einzigartigen Beziehung der Trolle zum Meer an.\nDAS GROSSE MEER\nDas Reich der Gurubashi wurde von drei Seiten vom Meer begrenzt. Da überrascht es nicht, dass dem Wasser in ihrer Gesellschaft eine herausragende Bedeutung zukam. Denn während die Trolle ohne Probleme große Gebiete ihres Dschungelreichs durchstreifen und kontrollieren konnten, entzog sich ihnen das Meer. Das Meer war groß und unermesslich' WHERE (`entry`='293');
UPDATE `itempages_localized` SET `text`='und deshalb eine beunruhigende Nachbarschaft für die mächtigen Trolle.\nEs sollte an dieser Stelle vermerkt werden, dass die jüngsten Entdeckungen vermuten lassen, dass die Trolle wenig Interesse an der Erforschung der Länder jenseits des Großen Meeres hatten. Während sich Spuren verschiedener Trollarten in ganz Azeroth, Khaz Modan und Lordaeron fanden, tauchten in den neu entdeckten Ländern von Kalimdor oder auf den Inseln der südlichen Meere nur wenige Zeugnisse ihrer Kultur auf. Ob dies nun als Zeichen dafür gewertet werden kann, dass' WHERE (`entry`='294');
UPDATE `itempages_localized` SET `text`='die Trolle ihre angestammten Behausungen nicht verlassen wollten, oder ob sie einfach nicht in der Lage waren, die Technologie für solche Reisen zu entwickeln, wird Gegenstand weiterer Forschungen und Analysen sein, die den Rahmen meiner Ausführungen hier sprengen würden.\nAber ihre Präsenz lässt sich nicht leugnen und neue Funde in den weitläufigen Trollruinen des Schlingendorntals verdeutlichen einen bislang unbekannten und undokumentierten Aspekt in ihrer Beziehung zum Meer.\nDER STEIN DER GEZEITEN\nJüngste Entdeckungen bei der Erforschung der' WHERE (`entry`='295');
UPDATE `itempages_localized` SET `text`='Trollruinen im Schlingendorntal haben einen Bezug zu einem Objekt mit dem Namen "Stein der Gezeiten" deutlich werden lassen. Wenn man verschiedene Fragmente der Legenden der Trolle zusammensetzt, entsteht daraus ein ziemlich vollständiges Bild von diesem Stein und seiner Bedeutung für das alte Reich der Gurubashi.\nEs scheint, als verleihe der Stein der Gezeiten seinem Träger die Macht über das Wasser in seinen verschiedensten Formen - Flüsse, Regen und eben die Gezeiten. Weil es so viele Geschichten um den Stein der Gezeiten gibt, habe ich daraus geschlossen, dass' WHERE (`entry`='296');
UPDATE `itempages_localized` SET `text`='es sich dabei um eine Manifestation des Wasserlords, eines mächtigen Elementars der Meere, handelt. Wie und warum ein solches Objekt dem Wasserlord entrissen werden und den Trollen in die Hände fallen konnte, ist eine weitere Frage, die mir Rätsel aufgibt.\nDER FÜRST DER GEZEITEN\nDer Stein der Gezeiten kam in die Welt der Trolle und verschwand wieder aus ihr, ganz wie die Gezeiten des Meeres, die ihm seinen Namen gegeben haben. Niemals blieb er länger als eine Generation in den Händen eines Trolls.' WHERE (`entry`='297');
UPDATE `itempages_localized` SET `text`='In den Legenden der Trolle heißt es, dass, als der Stein der Gezeiten zum ersten Mal in das Reich der Gurubashi kam, er von einem Trollkrieger gefunden worden sei, der am Strand des Schlingendorntals entlangwanderte. Er stieß auf einen merkwürdigen blauen Stein, in dem milchigweiße Schlieren und Streifen waberten. Fasziniert von dem Stein, nahm der Krieger ihn mit und ging weiter.\nIm Laufe der Zeit erkannte der Krieger, dass er durch den Stein das Wasser beherrschen konnte. Er konnte' WHERE (`entry`='298');
UPDATE `itempages_localized` SET `text`='damit Wasserelementare, also vollständig aus Wasser bestehende Wesen, beschwören. Zu dieser außergewöhnlichen Leistung sind normalerweise nur die mächtigen Magier der Kirin Tor fähig.\nDer Krieger reiste nach Zul''Gurub, ins Herz des Reichs, um dem Imperator seine neu gewonnenen Fähigkeiten zu zeigen. Nachdem er seine Macht im Zentrum der imperialen Hauptstadt demonstriert hatte, bekam er auch leicht eine Audienz bei Hofe. Der Imperator der Guribashi zeigte sich rasch beeindruckt von der Macht des Steins. Er verlieh dem Krieger einen ehrenhaften Posten bei Hofe, nannte ihn den "Fürst der Gezeiten" und wies ihn an,' WHERE (`entry`='299');
UPDATE `itempages_localized` SET `text`='seinen alten Namen zu vergessen.\nJahrelang diente der Fürst der Gezeiten dem Reich der Gurubashi. In der Schlacht beschwor er die geknechteten Wasserelementare, und er änderte den Lauf des Wassers im Umfeld des Schlingendorntals zum Wohle des Reichs. Aber mit den Jahren zog sich der Fürst der Gezeiten immer mehr zurück und blieb oft lange Zeit dem Hofe fern.\nEr versteckte sich vor den allzu neugierigen Augen bei Hof. Denn an die Fähigkeiten, die ihm der Stein der Gezeiten verliehen hatte, war auch ein Fluch geknüpft. Während die Jahre' WHERE (`entry`='300');
UPDATE `itempages_localized` SET `text`='vergingen, wurde der Fürst der Gezeiten immer weniger. Mit jedem Kommen und Gehen von Ebbe und Flut verblasste der Fürst der Gezeiten mehr. Er verlor seine Korporalgestalt, tauchte auf und verschwand, bis er schließlich am Ende seines Lebens an jenen Strand zurückkehrte, an dem er einst den Stein gefunden hatte. Dort ging er einfach ins Meer und tauchte nie mehr auf.\nGenerationen später wurde der Stein der Gezeiten erneut an die Küste des Schlingendorntals gespült und ein neuer Fürst der Gezeiten wurde erwählt, denn der Troll brachte den Stein nach' WHERE (`entry`='301');
UPDATE `itempages_localized` SET `text`='Zul''Gurub. So ging es Generationen weiter: Der Stein kam mit den Gezeiten und der Fürst der Gezeiten ging mit ihnen.\nNoch heute erreichen uns immer wieder Berichte vom Stein der Gezeiten aus allen Teilen der Welt, aber es erstaunt doch immer wieder, dass ein Objekt von solcher Macht mit solcher Regelmäßigkeit auftaucht, und man fragt sich, wer dahinter steckt.' WHERE (`entry`='302');
UPDATE `itempages_localized` SET `text`='Blutsegelbefehle\nBefolgt diese Worte hier genau, ihr Herzchen, denn wir haben nur einen Versuch, was Beutebucht angeht.\nIch habe den alten Trunkenbold als Vorhut in den Norden geschickt und ihm gesagt, er soll auf das Signal warten.\nDie "Maid im Glück" wird vor dem Südkap vor Anker gehen und Nachschub liefern. Die Mannschaft von Kapitän Stillwasser, unserem zu kurz geratenen Freund, wird die ganze geplünderte Beute an Bord nehmen. Ich bin mir sicher, dass sich der Schwachkopf Brutus der Sache annehmen wird.' WHERE (`entry`='303');
###
# 304 - 305
###
UPDATE `itempages_localized` SET `text`='Ich will, dass sich Käpt''n Kielhol mit der "Springflut" von Südosten her der Küste nähert. Wir schicken seine Mannschaft zu Fuß rein, wenn das Signal ertönt. \nSalzhuf, das Riesenbaby, soll mit den Männern entern. Ich will keine Gnade für Revilgaz und Seehorn. Macht Fischfutter aus ihnen, mir ist es gleich.\nJedenfalls seid ihr mir dafür verantwortlich, dass alles geplündert wird, was nicht niet- und nagelfest ist und uns bei unserem kleinen Vorhaben mit van Cleef in den Todesminen helfen könnte.' WHERE (`entry`='306');
UPDATE `itempages_localized` SET `text`='Ich werde Stahlauge Befehl geben, die Geschütze abzufeuern. Das Geschützsperrfeuer wird alle Schiffe der Schwarzmeerräuber am Ein- oder Auslaufen hindern.\nSobald die "Purpurschleier" ihre Geschütze abgefeuert hat, bringe ich sie längsseits der "Springflut", damit sie gegebenenfalls Unterstützung und Reserven zur Verfügung hat.\nWenn Salzhuf und seine Jungs die Stadt beschossen haben und alle Mann an Deck sind, setzen wir Segel und schicken der Bucht von der Ungezähmten Küste aus noch einmal ein paar deftige Grüße.\n- Flottenmeister Firallon' WHERE (`entry`='307');
UPDATE `itempages_localized` SET `text`='In diesem altersschwachen Boot vor den Zanzil zu fliehen, schien mir damals eine gute Entscheidung zu sein, aber inzwischen frage ich mich, ob der Tod nicht doch die bessere Wahl gewesen wäre.\nIch hatte geglaubt, meine Vorfahren täten mir einen Gefallen, indem sie mich vor ihrem Zorn schützten. Aber jetzt frage ich mich, was ich aus einem Leben in Gefangenschaft lernen soll.' WHERE (`entry`='308');
UPDATE `itempages_localized` SET `text`='Die Bestie, die über diese Insel herrscht, ist wirklich sehr mächtig. Alle anderen Wesen hier verstecken sich vor ihm. Weder die Blaumähnengorillas noch die Jaguero trauen ihm über den Weg. Sogar jetzt höre ich sein Brüllen und es macht mir Angst.\nDiese Kreaturen halten ganz sicher jeden ungebetenen Gast von der Insel fern. Ich muss Vertrauen in meine Vorfahren haben.' WHERE (`entry`='309');
UPDATE `itempages_localized` SET `text`='Ich vermisse meinen Stamm und mein Zuhause. Der Wind über der offenen Steppe oder der Sonnenuntergang über Donnerfels wären mir tausendmal lieber als die Hitze dieses Urwalds hier.\n\nIch kann sogar das Meer in nicht allzu weiter Ferne sehen, aber das verschafft weder meinem Körper noch meinem Temperament Kühlung. Es erinnert mich nur umso mehr daran, dass ich hier festsitze.' WHERE (`entry`='310');
###
# 311 - 314
###
UPDATE `itempages_localized` SET `text`='Es war weise vom Rat, Euch zum Vorsteher der Arguswacht und mithin zum Marschall seiner Strategie im Land der Menschen und Zwerge zu machen. Es ist mir eine Ehre, die ausführende Hand Eures Willens in Alterac zu sein.\nIm Namen von Sargeras,\nNagaz, \nHierophant des Argus' WHERE (`entry`='315');
UPDATE `itempages_localized` SET `text`='Ihr Stümper!\nMeine Pläne sahen die gleichzeitige Zerstörung beider Brücken vor. Und was macht ihr einfältigen Rohlinge? Ihr macht nur halbe Sachen und alarmiert so die Allianz über unser Vorhaben! Ihr Narren!\nIhr habt noch eine Chance, euren Fehler wieder auszubügeln. Wir haben noch eine Ladung Sprengkörper für den Notfall im Arathihochland. \nWenn euch euer mickriges Leben lieb ist, macht ihr es diesmal richtig!\n- Kam Tiefenzorn' WHERE (`entry`='316');
UPDATE `itempages_localized` SET `text`='Und wie eine Wassersäule erhob sich Neptulon aus dem Ozean, um die großen Krakken zu der verdammten Stadt I''lalai zu schicken. So groß waren die Krakken, dass ganze Wälder von Tang zwischen ihren Tentakeln Platz fanden und Leviathane durch ihre Körper schwammen. \nDann hob der größte Krakken seine Arme und ließ sie donnernd ins Meer schlagen, so dass gewaltige Wellen sich auftürmten. Wellen, die geradewegs auf I''lalai zurasten.' WHERE (`entry`='324');
UPDATE `itempages_localized` SET `text`='Die Krakken brüllten, und ihre Stimmen klangen wie das Brausen des Sturms über der See: \n"Wir kommen."\nMin''loth stand unerschütterlich und beschwor seine magischen Kräfte. Die zu I''lalia geschickten Wellen teilten sich und ergossen sich nach beiden Seiten, den Dschungel dahinter unter sich begrabend. Alsdann hieß Min''loth seine Diener Bindungsgesänge anstimmen, und Dutzende von Trollstimmen erhoben sich tosend.\nUnd eine Stimme war lauter als alle anderen.' WHERE (`entry`='325');
UPDATE `itempages_localized` SET `text`='Min''loth brüllte auf und seine Magie sammelte die Macht der Zauber seiner Diener und er schleuderte sie den nahenden Krakken entgegen.\nDie See teilte sich und Min''loths Zauber raste auf die Diener des Neptulon zu. Blitze zerrissen den Himmel, als der Zauber sie traf. Tausende Blitze zuckten herab, brachten das Wasser zum Kochen und gruben Krater in die Erde.\nMin''loth stieß ein Triumphgeheul aus, glaubte er doch, sein Zauber werde die gigantischen Krakken fällen.' WHERE (`entry`='326');
UPDATE `itempages_localized` SET `text`='Aber die Krakken sind alt, sehr alt. Sie waren dabei, als das Meer das Land gebar.\nSie waren dabei, als die Altvorderen herrschten, und als die Reisenden kamen, um sie zu stürzen. Sie waren dabei, als die Magie noch neu war.\nSie sind alt und hüten viele Geheimnisse. Min''loths Zauber war stark, aber nicht anders als der Troll war auch der Zauber sterblich. \nUnd so versagte er.' WHERE (`entry`='327');
UPDATE `itempages_localized` SET `text`='Zwar konnten die Krakken nicht gebunden werden, aber es machte sie wütend. Seit Ewigkeiten hatten Sterbliche ihnen keine Schmerzen mehr bereitet, und der Zauber der Trolle war wahrlich schmerzhaft.\nSie schüttelten den Bann von Minloths Zauber ab und schlugen dann zornig brüllend zurück.\nEin Rumpeln war zu vernehmen, als große Wellen aus der Tiefe emporstiegen und auf das Land zurasten. Als sie I''lalai erreichten, warfen sie einen Schatten auf die Stadt.\nAber bevor die Wellen sie zerstören konnten, hielten die Krakken plötzlich inne und verharrten unbeweglich.' WHERE (`entry`='328');
UPDATE `itempages_localized` SET `text`='Die Trollhexendoktoren erzitterten und wehklagten ihrem Meister. Min''loth starrte auf die Berge im Wasser, verloren und doch trotzig. Er drehte sich zu seinen Adepten um und flüsterte seine letzten Worte, welche die Trolle in Stein meißelten. Dann wandte sich Min''loth den drohenden Krakken zu.\nEr verzog das Gesicht zu einer Fratze und wirbelte in einem letzten kühnen Akt seinen Stab herum.\nDann richteten die Krakken all ihre Wut auf Min''loth und der Ozean stürzte auf I''lalai.\nUnd es war nicht mehr.' WHERE (`entry`='329');
UPDATE `itempages_localized` SET `text`='Und dann überspülte das Wasser den Urwald und riss alles mit sich fort. Trolle und Wildtiere heulten auf, bevor das Wasser sie erfasste und in die Tiefe zog.\nViele Gurubashi fragten sich noch, warum der Ozean sie verschlang, aber sie starben unwissend.\nErst als das Wasser die Berge erreichte, hielt es inne. Besänftigt zog sich das Wasser danach wieder zurück und hinterließ einen Schauplatz der Verwüstung.\nTrotz allem blieb I''lalai eine auf ewig versunkene Stadt.' WHERE (`entry`='330');
UPDATE `itempages_localized` SET `text`='Und Häuptling Var''gazul, hinter den Bergen in Zul''Gurub in Sicherheit, ging in den Urwald und fand dort keine Spur seines Volkes mehr.\nDa verzweifelte er, denn seine Träume von der Eroberung waren geplatzt.\nUnd Min''loth die Schlange ward niemals gefunden.' WHERE (`entry`='331');
###
# 332 - 335
###
UPDATE `itempages_localized` SET `text`='An die Schneiders,\nguten Tag. Ich hoffe, die Geschäfte im Kanaldistrikt laufen gut. Wir hier am Händlerplatz können uns über mangelnde Arbeit jedenfalls nicht beklagen.\nWir brauchen dringend neue Strickstoff-Waren, denn unser Vorrat geht zur Neige. Bitte schickt uns schnellstmöglich die übliche Ladung.\nGutes Gelingen! Ich hoffe, die Studien Eures Sohnes Thurman schreiten rasch voran,\n- Harlan Bagley' WHERE (`entry`='336');
UPDATE `itempages_localized` SET `text`='Offizielle Befehle für die Flotte von Kul Tiras\n\nIch, Admiral Prachtmeer, führe die erste Landung an der Küste von Kalimdor an.\n\nLeutnant Benedict führt in meiner Abwesenheit das Kommando. Sämtliche militärischen Angelegenheiten hier vor Ort in Kul Tiras werden von ihm geregelt. Sollte ich binnen der nächsten zwölf Monate nichts aus Burg Tiragarde hören lassen, führt Leutnant Benedict die zweite Welle zur Eroberung der Stellung an.' WHERE (`entry`='337');
UPDATE `itempages_localized` SET `text`='In der Zwischenzeit schicke ich Leutnant Alverold auf eine Erkundungsmission rund um den Kontinent Kalimdor. Nach Abschluss der Mission sollen sich seine Truppen wieder den unseren bei Burg Tiragarde anschließen.\n\nEs ist allerdings davon auszugehen, dass Leutnant Alverolds Mission einige Zeit dauern wird. Das neue Land macht einen ebenso weiten wie gefährlichen Eindruck. Sollte Benedict oder mir etwas zustoßen, werden Alverolds Truppen die Burg zurückerobern.\n\nWir werden Welle auf Welle schicken, bis die Festung unser ist!\n\n- Admiral Prachtmeer' WHERE (`entry`='338');
UPDATE `itempages_localized` SET `text`='In der Zwischenzeit schicke ich Lieutenant Alverold auf eine Erkundungsmission rund um den Kontinent Kalimdor. Nach Abschluss der Mission sollen sich seine Truppen wieder den unseren bei Burg Tiragarde anschließen. Es ist allerdings davon auszugehen, dass Lieutenant Alverolds Mission einige Zeit dauern wird. Das neue Land macht einen ebenso weiten wie gefährlichen Eindruck. Sollte Benedict oder mir etwas zustoßen, werden Alverolds Truppen die Burg zurückerobern. Wir werden Welle auf Welle schicken, bis die Festung unser ist! - Admiral Proudmoore' WHERE (`entry`='339');
